SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_lotes_agrupados ( cd_setor_atendimento_p bigint, nr_seq_turno_p bigint, nr_sequencia_p bigint, dt_inicio_p timestamp, dt_final_p timestamp, nm_usuario_p text, nr_seq_classif_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


qt_registros_w		bigint;
qt_status_w		bigint;
ds_erro_w		varchar(255);



BEGIN

select	count(*)
into STRICT	qt_registros_w
from   	regra_turno_disp b,
	ap_lote a
where   a.nr_seq_turno  = b.nr_sequencia
and (b.nr_sequencia	= nr_seq_turno_p or nr_seq_turno_p = '0')
and (a.cd_setor_atendimento = cd_setor_atendimento_p or cd_setor_atendimento_p = '0')
and (a.nr_seq_classif  = nr_seq_classif_p or nr_seq_classif_p = '0')
and    	a.ie_status_lote  = 'A'
and	trunc(a.dt_atend_lote, 'dd') between dt_inicio_p and dt_final_p;

if (qt_registros_w > 0) then
	ds_erro_w := ds_erro_w || WHEB_MENSAGEM_PCK.get_texto(281441);
end if;


select	count(*)
into STRICT	qt_status_w
from	ap_lote_agrup_item
where	ie_status = 'P'
and	nr_seq_agrup = nr_sequencia_p;

if (qt_status_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265960);
	--'Existem lotes que não estão consistidos por barras!'
end if;

ds_erro_p := ds_erro_w;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
