SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_lote_dirf ( nr_sequencia_p bigint, retorno_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Consistir para que não seja permitido gerar o lote caso o mês já possui um lote gerado
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_estabelecimento_w		dirf_lote_mensal.cd_estabelecimento%type;
qt_registros_w			bigint;
dt_mes_referencia_w		timestamp;
dt_mes_referencia_fm_w		timestamp;


BEGIN
select	a.dt_mes_referencia,
	a.cd_estabelecimento
into STRICT	dt_mes_referencia_w,
	cd_estabelecimento_w
from	dirf_lote_mensal	a
where	a.nr_sequencia	= nr_sequencia_p;

dt_mes_referencia_fm_w	:= fim_dia(fim_mes(PKG_DATE_UTILS.ADD_MONTH(dt_mes_referencia_w, -1, 0)));
dt_mes_referencia_w	:= trunc(dt_mes_referencia_w,'yyyy');

select	count(1)
into STRICT	qt_registros_w
from	dirf_lote_mensal	a
where	cd_estabelecimento	= cd_estabelecimento_w
and	coalesce(dt_geracao::text, '') = ''
and	a.dt_mes_referencia between dt_mes_referencia_w and dt_mes_referencia_fm_w  LIMIT 1;

if (qt_registros_w > 0) then
	retorno_p	:= 'F';
else
	retorno_p	:= 'V';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_lote_dirf ( nr_sequencia_p bigint, retorno_p INOUT text) FROM PUBLIC;

