CREATE OR REPLACE PROCEDURE consistir_lote_dmed_anual ( nr_seq_dmed_anual_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE

nr_recibo_w		varchar(20);


BEGIN

-- verificar se a sequencia  anual já tem recibo informado
select	nr_recibo
into STRICT	nr_recibo_w
from	dmed
where	nr_sequencia = nr_seq_dmed_anual_p;

ds_retorno_p := '';

if (nr_recibo_w IS NOT NULL AND nr_recibo_w::text <> '') then -- se já tem recibo informado
	ds_retorno_p := 'Não é possivel desfazer o lote anual. O recibo já foi informado (DMED já entregue ao governo).';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
