CREATE OR REPLACE PROCEDURE consistir_margem_adm_palm ( nr_seq_horario_p bigint, ie_tipo_item_p text, dt_evento_p timestamp, qt_min_margem_adm_p bigint, cd_perfil_p bigint, ie_possui_evento_p text, ie_acao_adm_fora_margem_p text, ds_retorno_p INOUT text, nm_usuario_p text, ds_justificativa_p text, ie_obriga_justificativa_p text, qt_min_margem_ant_p bigint) AS $body$
DECLARE


ie_margem_valida_w	varchar(1);
qt_minutos_w		bigint;
ds_retorno_w		varchar(255);


BEGIN


ie_margem_valida_w := consistir_margem_administracao(nr_seq_horario_p, ie_tipo_item_p, dt_evento_p, qt_min_margem_adm_p,cd_perfil_p, 'S', qt_min_margem_ant_p,0,null);

qt_minutos_w := consistir_margem_administracao(nr_seq_horario_p, ie_tipo_item_p, dt_evento_p, qt_min_margem_adm_p,cd_perfil_p, 'M',qt_min_margem_ant_p,0,null);

if	('N' = ie_margem_valida_w AND 'NP' = ie_acao_adm_fora_margem_p) then
	ds_retorno_w := /*'A margem máxima permitida para administração dos horários, em relação ao horário original, é de '||qt_minutos_w||'.Você não tem permissão para a realização desta administração!'*/
 wheb_mensagem_pck.get_texto(10669, 'margem=' || qt_minutos_w);
elsif 	(('N' = ie_margem_valida_w)  and ('EJ' = ie_acao_adm_fora_margem_p) and ('N' = ie_possui_evento_p)) then
	ds_retorno_w := /*'A margem máxima permitida para administração dos horários, em relação ao horário original, é de '||qt_minutos_w||' minutos.Você deve informar uma justificativa para a realização desta administração!'*/
 wheb_mensagem_pck.get_texto(10671, 'margem=' || qt_minutos_w);
end if;

if (ie_acao_adm_fora_margem_p = 'EJ') then
	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
		if (ie_obriga_justificativa_p = 'S') and (coalesce(ds_justificativa_p::text, '') = '') then
			ds_retorno_p := ds_retorno_w;
		elsif (ie_obriga_justificativa_p = 'S') and (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> '') then
			ds_retorno_p := null;
		elsif (coalesce(ie_obriga_justificativa_p,'N') = 'N') then
			ds_retorno_p := null;
		end if;
	end if;
else
	ds_retorno_p := ds_retorno_w;
end if;

commit;

end;
$body$
