SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_material_solucao ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_sequencia_p bigint, qt_volume_hidrico_p bigint, cd_material_p bigint, cd_unidade_medida_dose_p text, qt_dose_p INOUT bigint, ie_convert_dose_quant_p INOUT text, nm_usuario_p text) AS $body$
DECLARE


ie_solucao_w			  	varchar(1) := 'N';
ie_ancora_w				  	varchar(1) := 'N';
ie_existe_material_w		varchar(1);
nr_seq_ancora_w				bigint;
nr_seq_comp_w				bigint;
qt_dose_convert_w			double precision;
qt_dose_w					double precision;
ie_convert_dose_quant_w 	varchar(1) := 'N';
varUtilizaHidro_w			varchar(1);
cd_perfil_w					bigint;
cd_estabelecimento_w		bigint;
cd_material_w		    	prescr_material.cd_material%type;
qt_dose_ww					prescr_material.qt_dose%type;
cd_unidade_medida_dose_w	prescr_material.cd_unidade_medida_dose%type;

BEGIN

cd_perfil_w				:= coalesce(obter_perfil_ativo,0);
cd_estabelecimento_w	:= coalesce(wheb_usuario_pck.get_cd_estabelecimento,0);

CALL wheb_assist_pck.set_informacoes_usuario(cd_estabelecimento_w,cd_perfil_w,nm_usuario_p);

varUtilizaHidro_w := wheb_assist_pck.obterValorParametroREP(811, varUtilizaHidro_w);

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	coalesce(max('S'),'N')
	into STRICT	ie_existe_material_w
	from	material
	where	cd_material = cd_material_p;

	if (ie_existe_material_w = 'N') then
		begin
		-- Não existe medicação com este código!
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(103040);
		end;
	end if;

	select	coalesce(max(ie_solucao),'N')
	into STRICT	ie_solucao_w
	from	material
	where	cd_material	= cd_material_p;

	if ('N' = ie_solucao_w) then
		begin
		-- A medicação não pode ser prescrita para solução.
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(103040);
		end;
	end if;

	if (qt_volume_hidrico_p IS NOT NULL AND qt_volume_hidrico_p::text <> '') then
		begin

		if (varUtilizaHidro_w = 'S') then

			select	coalesce(ie_ancora_solucao,'N')
			into STRICT	ie_ancora_w
			from	material
			where	cd_material	= cd_material_p;

			select	coalesce(min(b.nr_sequencia),0)
			into STRICT	nr_seq_ancora_w
			from	material a,
				prescr_material b
			where	a.cd_material	    = b.cd_material
			and	b.nr_prescricao	        = nr_prescricao_p
			and	b.nr_sequencia_solucao	= nr_seq_solucao_p
			and	b.ie_agrupador	        = 4
			and	a.ie_ancora_solucao	    = 'S';

			select	coalesce(min(b.nr_sequencia),0)
			into STRICT	nr_seq_comp_w
			from	material a,
				prescr_material b
			where	a.cd_material	    = b.cd_material
			and	b.nr_prescricao	        = nr_prescricao_p
			and	b.nr_sequencia_solucao	= nr_seq_solucao_p
			and	b.ie_agrupador	        = 4
			and	coalesce(a.ie_ancora_solucao,'N') = 'N';

			select coalesce(max(cd_material),0),
				   coalesce(max(qt_dose),0),
				   coalesce(max(cd_unidade_medida_dose),0)
			into STRICT   cd_material_w,
				   qt_dose_ww,
				   cd_unidade_medida_dose_w
			from  prescr_material
			where nr_prescricao 	  = nr_prescricao_p
			and	nr_sequencia_solucao  = nr_seq_solucao_p
			and	nr_sequencia		  = coalesce(nr_seq_ancora_w,0);

			select coalesce(obter_dose_convertida(cd_material_w,qt_dose_ww,cd_unidade_medida_dose_w,obter_unid_med_usua('ml')),0)
			into STRICT   qt_dose_convert_w
			;

		elsif (varUtilizaHidro_w = 'T') then

			select   coalesce(min(nr_sequencia),nr_sequencia_p)
			into STRICT	 nr_seq_ancora_w
            from     prescr_material
            where    nr_prescricao 		  = nr_prescricao_p
            and      nr_sequencia_solucao = nr_seq_solucao_p
            and      ie_agrupador   	  = 4;

			if (nr_seq_ancora_w = nr_sequencia_p) then
				ie_ancora_w	:= 'S';
			else
				ie_ancora_w	:= 'N';
			end if;

			select   coalesce(min(nr_sequencia),0)
			into STRICT	 nr_seq_comp_w
            from     prescr_material
            where    nr_prescricao 		  = nr_prescricao_p
            and      nr_sequencia_solucao = nr_seq_solucao_p
            and      ie_agrupador  		  = 4
            and      nr_sequencia 		  > nr_seq_ancora_w;

			select coalesce(max(cd_material),0),
				   coalesce(max(qt_dose),0),
				   coalesce(max(cd_unidade_medida_dose),0)
			into STRICT   cd_material_w,
				   qt_dose_ww,
				   cd_unidade_medida_dose_w
			from  prescr_material
			where nr_prescricao 	  = nr_prescricao_p
			and	nr_sequencia_solucao  = nr_seq_solucao_p
			and	nr_sequencia		  = coalesce(nr_seq_ancora_w,0);

			select coalesce(obter_dose_convertida(cd_material_w,qt_dose_ww,cd_unidade_medida_dose_w,obter_unid_med_usua('ml')),0)
			into STRICT   qt_dose_convert_w
			;

		end if;

		if (ie_ancora_w = 'N') and (nr_seq_ancora_w > 0) and
			((nr_seq_comp_w <> nr_sequencia_p AND nr_seq_comp_w > 0) or (qt_volume_hidrico_p - qt_dose_convert_w) < 0) then
			begin
			qt_dose_w := 0;
			end;
		elsif (ie_ancora_w = 'N') and (nr_seq_ancora_w > 0) and
			((nr_seq_comp_w	= 0) or (nr_seq_comp_w	= nr_sequencia_p)) then
			begin
			select	coalesce(obter_dose_convertida(cd_material,qt_volume_hidrico_p - qt_dose_convert_w,cd_unidade_medida_dose,obter_unid_med_usua('ml')),0)
			into STRICT	qt_dose_w
			from	prescr_material
			where	nr_prescricao	= nr_prescricao_p
			and		nr_sequencia	= coalesce(nr_seq_ancora_w,0);
			end;
		end if;

		if (ie_ancora_w = 'N') and (nr_seq_ancora_w > 0) then
			begin
			ie_convert_dose_quant_w := 'S';
			end;
		end if;
		end;
	end if;
	end;
end if;
commit;
qt_dose_p				:= qt_dose_w;
ie_convert_dose_quant_p	:= ie_convert_dose_quant_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_material_solucao ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_sequencia_p bigint, qt_volume_hidrico_p bigint, cd_material_p bigint, cd_unidade_medida_dose_p text, qt_dose_p INOUT bigint, ie_convert_dose_quant_p INOUT text, nm_usuario_p text) FROM PUBLIC;

