CREATE OR REPLACE PROCEDURE consistir_material_substituto ( cd_material_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE


ds_retorno_w		varchar(4000);
cd_material_w		integer;
ie_situacao_w		varchar(1);
ie_material_estoque_w	varchar(1);
ie_prescricao_w		varchar(1);
ie_padronizado_w	varchar(1);


BEGIN

select	max(a.cd_material),
	max(a.ie_situacao),
	max(b.ie_material_estoque),
	max(b.ie_prescricao),
	max(b.ie_padronizado)
into STRICT	cd_material_w,
	ie_situacao_w,
	ie_material_estoque_w,
	ie_prescricao_w,
	ie_padronizado_w
from	material_estab b,
	material a
where	a.cd_material		= b.cd_material
and	a.cd_material		= cd_material_p
and	b.cd_estabelecimento	= cd_estabelecimento_p;

ds_retorno_w	:= '';

if (coalesce(cd_material_w,0)	= 0) then
	ds_retorno_w	:= wheb_mensagem_pck.get_texto(313596);
end if;
if (coalesce(ds_retorno_w::text, '') = '') then
	if (ie_situacao_w	= 'N') then
		ds_retorno_w	:= ' ' || wheb_mensagem_pck.get_texto(313597);
	end if;
	if (ie_material_estoque_w	= 'N') then
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= ' ' || wheb_mensagem_pck.get_texto(313598);
		else
			ds_retorno_w	:= ds_retorno_w||chr(13)||chr(10)|| ' ' || wheb_mensagem_pck.get_texto(313598);
		end if;
	end if;
	if (ie_prescricao_w	= 'N') then
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= ' ' || wheb_mensagem_pck.get_texto(313599);
		else
			ds_retorno_w	:= ds_retorno_w||chr(13)||chr(10)||' ' || wheb_mensagem_pck.get_texto(313599);
		end if;
	end if;
	if (ie_padronizado_w	= 'N') then
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= ' ' || wheb_mensagem_pck.get_texto(313601);
		else
			ds_retorno_w	:= ds_retorno_w||chr(13)||chr(10)||' ' || wheb_mensagem_pck.get_texto(313601);
		end if;
	end if;
end if;

ds_retorno_p	:= ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
