CREATE OR REPLACE PROCEDURE consistir_mat_lanc_cirurgia ( cd_material_p bigint, cd_convenio_p bigint, cd_fornecedor_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_regra_w				bigint;
cd_grupo_material_w		bigint;
cd_grupo_w				numeric(20);
cd_classe_w				numeric(20);
cd_subgrupo_w			numeric(20);


BEGIN
if (coalesce(cd_material_p,0) > 0) then

	select	max(substr(obter_estrutura_material(cd_material,'G'),1,20)),
			max(substr(obter_estrutura_material(cd_material,'C'),1,20)),
			max(substr(obter_estrutura_material(cd_material,'S'),1,20))
	into STRICT	cd_grupo_w,
			cd_classe_w,
			cd_subgrupo_w
	from	material
	where	cd_material		= cd_material_p;

	select	count(*)
	into STRICT	qt_regra_w
	from	regra_boqueio_mat_cirur
	where	coalesce(cd_material,cd_material_p) = cd_material_p
	and		coalesce(cd_grupo_material,cd_grupo_w) = cd_grupo_w
	and		coalesce(cd_subgrupo_material,cd_subgrupo_w) = cd_subgrupo_w
	and		coalesce(cd_classe_material,cd_classe_w) = cd_classe_w
	and		coalesce(cd_convenio,cd_convenio_p) = cd_convenio_p
	and		coalesce(cd_fornec_consignado,coalesce(cd_fornecedor_p,0)) = coalesce(cd_fornecedor_p,0);



	if (qt_regra_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(246607);
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
