SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_medic_sem_apresent ( nr_seq_atendimento_p bigint) AS $body$
DECLARE


ds_material_w		varchar(255);
ie_tipo_material_w	varchar(10);
cd_material_w		bigint;
nr_seq_ficha_tecnica_w	bigint;
cont_w			bigint;

c01 CURSOR FOR
SELECT	substr(obter_Desc_material(a.cd_material),1,80),
	a.cd_material,
	b.ie_tipo_material,
	b.nr_seq_ficha_tecnica
from 	material b,
	paciente_atend_medic a
where	a.nr_seq_atendimento 	= nr_seq_atendimento_p
and	a.cd_material 		= b.cd_material
and	(b.nr_seq_ficha_tecnica IS NOT NULL AND b.nr_seq_ficha_tecnica::text <> '')
and	b.ie_tipo_material	= '6'
order by nr_seq_material;


BEGIN

open c01;
loop
fetch c01 into
	ds_material_w,
	cd_material_w,
	ie_tipo_material_w,
	nr_seq_ficha_tecnica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	select	coalesce(count(*),0)
	into STRICT	cont_w
	from	material b
	where	b.nr_seq_ficha_tecnica	= nr_seq_ficha_tecnica_w
	and	b.ie_situacao		= 'A'
	and	cd_material		<> cd_material_w;

	if (cont_w	= 0) then
		-- O item ' || ds_material_w || ' não tem nenhum medicamento comercial com o mesmo princípio ativo vinculado, favor verificar! #@#@');
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(261393, 'MATERIAL='|| ds_material_w);
	end if;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
