SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_mes_lote_dirf ( nr_sequencia_p bigint, dt_referencia_p timestamp, retorno_p INOUT text, cd_estabelecimento_p bigint) AS $body$
DECLARE


dt_geracao_maior_w	timestamp;
dt_referencia_maior_w	timestamp;
nr_sequencia_maior_W	bigint;
qt_registros_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	dirf_lote_mensal
where	cd_estabelecimento = cd_estabelecimento_p;

if (qt_registros_w = 0) then -- Representa que a tabela está vazia, portanto, não é necessário testar as consistências
	retorno_p := 'V';
else
	select	max(dt_mes_referencia)
	into STRICT	dt_referencia_maior_w
	from	dirf_lote_mensal
	where	cd_estabelecimento = cd_estabelecimento_p;

	select	dt_geracao,
		nr_sequencia
	into STRICT	dt_geracao_maior_w,
		nr_sequencia_maior_w
	from	dirf_lote_mensal
	where	dt_mes_referencia = dt_referencia_maior_w
	and	cd_estabelecimento = cd_estabelecimento_p;

	if (dt_referencia_p < dt_referencia_maior_w) then

		select	count(*)
		into STRICT	qt_registros_w
		from	dirf_lote_mensal
		where	dt_mes_referencia > dt_referencia_p
		and	(dt_geracao IS NOT NULL AND dt_geracao::text <> '')
		and	cd_estabelecimento = cd_estabelecimento_p;

		if (qt_registros_w <> 0) then --Existem lotes posteriores já gerados, portanto, não é possível fazer alterações.
			retorno_p := 'F';
			CALL wheb_mensagem_pck.exibir_mensagem_abort(266527,'MES=' || dt_referencia_p ||';' ||
								'MES_MAIOR=' || dt_referencia_maior_w);
		else
			retorno_p := 'V'; -- Existem lotes posteriores, porém ainda não foram gerados. Desta forma, é possível realizar as alterações.
		end if;

	elsif (dt_referencia_p = dt_referencia_maior_w) then
		if ((nr_sequencia_p = nr_sequencia_maior_w) and (coalesce(dt_geracao_maior_w::text, '') = '')) then -- Só pode fazer alteração no lote que ainda não foi gerado
			retorno_p := 'V';
		else
			retorno_p := 'F'; -- Se o lote já foi gerado não pode ser alterado.
			CALL wheb_mensagem_pck.exibir_mensagem_abort(266532,'MES=' || to_char(dt_referencia_p,'dd/mm/yyyy'));
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
