SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_nf_emprestimo ( nr_sequencia_nf_p bigint) AS $body$
DECLARE


--EMPRESTIMO
nr_emprestimo_w		emprestimo.nr_emprestimo%type;

--EMPRESTIMO_MATERIAL
cd_material_w		emprestimo_material.cd_material%type;
qt_material_w		emprestimo_material.qt_material%type;

--NOTA_FISCAL_ITEM
qt_item_nf_w		nota_fiscal_item.qt_item_nf%type;
cd_material_nf_w	nota_fiscal_item.cd_material%type;

c01 CURSOR FOR
	SELECT	nr_emprestimo
	from	nota_fiscal_item
	where	nr_sequencia = nr_sequencia_nf_p
	group by nr_emprestimo;

c02 CURSOR FOR
	SELECT	cd_material,
		sum(qt_material)
	from	emprestimo_material a
	where	nr_emprestimo = nr_emprestimo_w
	group by cd_material;


BEGIN

open c01;
loop
fetch c01 into
	nr_emprestimo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	open c02;
	loop
	fetch c02 into
		cd_material_w,
		qt_material_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin

		select	sum(qt_item_nf)
		into STRICT	qt_item_nf_w
		from	nota_fiscal_item
		where	nr_sequencia = nr_sequencia_nf_p
		and	cd_material = cd_material_w;

		if (qt_material_w <> qt_item_nf_w) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(283953,'CD_MATERIAL=' || cd_material_w || ';QT_MATERIAL_NF=' || qt_item_nf_w || ';QT_EMPRESTIMO=' || qt_material_w);
		end if;

		end;
	end loop;
	close c02;

	select	max(cd_material)
	into STRICT	cd_material_nf_w
	from	nota_fiscal_item a
	where	nr_sequencia = nr_sequencia_nf_p
	and	coalesce(nr_emprestimo::text, '') = ''
	and	coalesce(nr_seq_item_emprestimo::text, '') = '';

	if (cd_material_nf_w IS NOT NULL AND cd_material_nf_w::text <> '') then
		--Esta nota foi gerada a partir de um empréstimo e o material #@CD_MATERIAL#@ não possui empréstimo associado.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(283954,'CD_MATERIAL=' || cd_material_nf_w);
	end if;

	end;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_nf_emprestimo ( nr_sequencia_nf_p bigint) FROM PUBLIC;

