CREATE OR REPLACE PROCEDURE consistir_novo_saldo_banco ( dt_referencia_p timestamp) AS $body$
BEGIN

if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then

	if ( trunc(dt_referencia_p,'dd') <> trunc(dt_referencia_p,'month')  ) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(322195);
	end if;
end if;

end;
$body$
