CREATE OR REPLACE PROCEDURE consistir_paciente_espera ( nr_seq_pac_espera_p bigint, cd_pessoa_fisica_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_consistir_duplic_p text, ds_retorno_erro_p INOUT text, ds_retorno_info_p INOUT text) AS $body$
DECLARE


qt_atendimento_vinc_w		integer;
ie_paciente_fila_w		varchar(1);


BEGIN

select	count(*)
into STRICT	qt_atendimento_vinc_w
from	paciente_espera a
where	coalesce(a.nr_atendimento::text, '') = ''
and	a.ie_situacao <> 'C'
and	a.cd_pessoa_fisica	=  cd_pessoa_fisica_p
and	a.nr_sequencia 		<> nr_seq_pac_espera_p;

if (qt_atendimento_vinc_w > 0) and (ie_consistir_duplic_p = 'S') then
	select	substr(obter_texto_tasy(62950,wheb_usuario_pck.get_nr_seq_idioma),1,255)
	into STRICT	ds_retorno_info_p
	;
end if;

select	obter_se_pac_fila_espera_seq(	cd_pessoa_fisica_p,
					cd_procedimento_p,
					ie_origem_proced_p,
					nr_seq_pac_espera_p) ie_fila_espera
into STRICT	ie_paciente_fila_w
;

if (ie_paciente_fila_w	= 'S') then
	select	substr(obter_texto_tasy(62188,wheb_usuario_pck.get_nr_seq_idioma),1,255)
	into STRICT	ds_retorno_erro_p
	;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
