SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_pac_atend_grupo ( cd_pessoa_fisica_p text, --nr_atendimento_p	number,
 ds_Retorno_p INOUT text, nm_usuario_p text) AS $body$
DECLARE


ds_grupo_w	varchar(60);
nr_seq_grupo_w	bigint;


BEGIN
begin
	select	c.nr_seq_grupo
	into STRICT	nr_seq_grupo_w
	from	regra_agenda_grupo_atend c
	where	c.ie_situacao	= 'A'  LIMIT 1;
exception
	when others then
	nr_seq_grupo_w	:= null;
end;

if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	max(b.ds_grupo)
	into STRICT	ds_grupo_w
	from	pac_grupo_atend a,
		pac_grupo b
	where	a.nr_seq_grupo	= b.nr_sequencia
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	--and	(a.nr_atendimento	= nr_atendimento_p or a.nr_atendimento	= 0)
	and	a.nr_seq_grupo in (
				SELECT	c.nr_seq_grupo
				from	regra_agenda_grupo_atend c
				where	c.ie_situacao	= 'A');
end if;

if (ds_grupo_w IS NOT NULL AND ds_grupo_w::text <> '') then
	ds_retorno_p	:= Wheb_mensagem_pck.get_texto(306543, 'DS_GRUPO_W='||ds_grupo_w); -- 'O paciente est√° vinculado ao grupo '||ds_grupo_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
