SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_pac_lista_espera ( cd_pessoa_fisica_p text, nm_usuario_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE


nr_sequencia_w				bigint;
ie_cons_pac_lista_espera	varchar(1) := 'N';
dt_inclusao_w				timestamp;


BEGIN
ds_mensagem_p := null;

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select  max(nr_sequencia),
			max(dt_inclusao)
	into STRICT  	nr_sequencia_w,
			dt_inclusao_w
	from 	paciente_espera
	where   coalesce(nr_seq_agenda::text, '') = ''
	and 	coalesce(nr_atendimento::text, '') = ''
	and 	ie_situacao <> 'C'
	and     cd_pessoa_fisica = cd_pessoa_fisica_p;
end if;

if (coalesce(nr_sequencia_w,0) > 0) then
		ds_mensagem_p := wheb_mensagem_pck.get_texto(300457, 'DT_INCLUSAO_W=' || to_char(dt_inclusao_w,'dd/mm/yyyy hh24:mi:ss'));
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
