CREATE OR REPLACE PROCEDURE consistir_pat_bem ( dt_inicio_uso_p timestamp, dt_aquisicao_p timestamp, ie_consiste_bem_p text, ie_imobilizado_p text, ie_propriedade_p text, ie_consiste_emp_p text, ie_consiste_codigo_emp_p text, ie_novo_reg_p text, ie_edit_reg_p text, ie_tipo_valor_p text, nr_seq_bem_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text, nr_seq_local_p bigint, nr_seq_tipo_p bigint, nr_seq_princ_p bigint, cd_conta_contabil_p text, cd_centro_custo_p bigint, cd_bem_p text, cd_empresa_p bigint, nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_bem_w			bigint;
ie_consistir_w			bigint;
cd_estabelecimento_w		smallint;
cd_empresa_w			smallint;
ie_result_w			varchar(1);
ie_vinculo_bem_w		varchar(1);
ie_ficha_aceite_w		varchar(1);
ie_consiste_codigo_emp_w	varchar(1);


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	ie_consistir_w	:= 0;

	if (ie_consiste_emp_p IS NOT NULL AND ie_consiste_emp_p::text <> '') then
		begin
		select	count(*)
		into STRICT	ie_consistir_w
		from	estabelecimento b,
			pat_bem a
		where	a.cd_estabelecimento	= b.cd_estabelecimento
		and	a.cd_bem		= cd_bem_p
		and	a.nr_sequencia		<> nr_sequencia_p
		and	b.cd_empresa		= cd_empresa_p;

		if (ie_consistir_w	> 0) then
			begin
			CALL wheb_mensagem_pck.exibir_mensagem_abort(44463);
			end;
		end if;
		end;
	end if;

	ie_consistir_w	:= 0;

	select	cd_estabelecimento
	into STRICT	cd_estabelecimento_w
	from	centro_custo
	where	cd_centro_custo = cd_centro_custo_p;

	if (cd_estabelecimento_p <> cd_estabelecimento_w) then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44305);
		end;
	end if;

	ie_consistir_w	:= 0;

	select	count(*)
	into STRICT	ie_consistir_w
	from	pat_conta_contabil
	where	cd_conta_contabil = cd_conta_contabil_p;

	if (ie_consistir_w = 0) then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44306);
		end;
	end if;

	ie_consistir_w	:= 0;

	select  count(*)
	into STRICT	ie_consistir_w
	from    estabelecimento b,
        	pat_bem a
	where   a.cd_estabelecimento	= b.cd_estabelecimento
	and     a.cd_bem		= cd_bem_p
	and     nr_sequencia            <> nr_sequencia_p
	and     b.cd_empresa            = cd_empresa_p;

	ie_consiste_codigo_emp_w	:= substr(coalesce(ie_consiste_codigo_emp_p,'N'),1,1);
	if (ie_consistir_w > 0) and
		((ie_consiste_codigo_emp_w = 'S') or
		  (ie_consiste_codigo_emp_w = 'O' AND ie_tipo_valor_p = 'O')) then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44463);
		end;
	end if;

	ie_result_w	:= substr(coalesce(calcular_mes_pat_bem(dt_inicio_uso_p, cd_estabelecimento_p),'N'),1,1);

	if (ie_result_w = 'V') and (ie_novo_reg_p IS NOT NULL AND ie_novo_reg_p::text <> '') then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44307, 'DT_INICIO_USO=' || to_char(dt_inicio_uso_p, 'MM/yyyy'));
		end;
	end if;

	if (dt_inicio_uso_p < dt_aquisicao_p) then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44308);
		end;
	end if;

	if (ie_tipo_valor_p <> 'O') and (coalesce(nr_seq_princ_p,0) = 0) then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44309);
		end;
	end if;

	select	coalesce(a.cd_estabelecimento, 0)
	into STRICT	cd_estabelecimento_w
	from	pat_tipo_bem b,
		pat_grupo_tipo a
	where	a.nr_sequencia	= b.nr_seq_grupo
	and	b.nr_sequencia	= nr_seq_tipo_p;


	select	coalesce(a.cd_empresa, 0)
	into STRICT	cd_empresa_w
	from	pat_tipo_bem b,
		pat_grupo_tipo a
	where	a.nr_sequencia	= b.nr_seq_grupo
	and	b.nr_sequencia	= nr_seq_tipo_p;

	if (cd_estabelecimento_w <> 0) and (cd_estabelecimento_w <> cd_estabelecimento_p) then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44310);
		end;
	end if;

	if (cd_estabelecimento_w <> 0) and (cd_estabelecimento_w <> cd_estabelecimento_p) then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44311);
		end;
	end if;

	select	coalesce(cd_estabelecimento, 0)
	into STRICT	cd_estabelecimento_w
	from	pat_local
	where	nr_sequencia = nr_seq_local_p;

	if (cd_estabelecimento_w <> 0) and (cd_estabelecimento_w <> cd_estabelecimento_p) then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44312);
		end;
	end if;

	if (ie_propriedade_p  <> 'P') and (cd_cgc_p	= '') and (cd_pessoa_fisica_p = '') then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44313);
		end;
	end if;

	select	coalesce(ie_vinculo_bem,'N')
	into STRICT	ie_vinculo_bem_w
	from	pat_local
	where	nr_sequencia	= nr_seq_local_p;

	if (ie_vinculo_bem_w = 'N') then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(16585);
		end;
	end if;

	if (ie_consiste_bem_p = 'S') and (ie_imobilizado_p = 'S') then
		begin
		ie_ficha_aceite_w := substr(coalesce(pat_obter_se_ficha_aceite(cd_estabelecimento_p,nr_seq_bem_p,nr_seq_local_p,nr_seq_tipo_p),'N'),1,1);

		if (ie_ficha_aceite_w = 'S') then
			begin
			if (ie_edit_reg_p = 'S') then
				begin

				select	count(*)
				into STRICT	qt_bem_w
				from	pat_ficha_aceite a,
					pat_bem b
				where	b.nr_sequencia	= a.nr_seq_bem
				and	b.nr_seq_local	= a.nr_seq_local
				and	a.ie_aceite	= 'A'
				and	b.nr_sequencia	= nr_seq_bem_p;

				if (qt_bem_w = 0) then
					begin
					CALL wheb_mensagem_pck.exibir_mensagem_abort(48757);
					end;
				end if;
				end;
			end if;
			end;
		end if;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
