CREATE OR REPLACE PROCEDURE consistir_pat_hist_bem ( nr_sequencia_p bigint, nr_seq_hist_p bigint, nr_seq_tipo_p bigint, ie_excluir_historico_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ult_transf_w			bigint := 0;
ie_transferencia_w			varchar(1) := 'N';


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	if (ie_excluir_historico_p <> 'S') then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44356);
		end;
	end if;

	select	coalesce(max(a.nr_sequencia),0)
	into STRICT	nr_seq_ult_transf_w
	from	pat_historico_bem a,
		pat_tipo_historico b
	where	a.nr_seq_bem 		= nr_sequencia_p
	and	a.nr_seq_tipo 		= b.nr_sequencia
	and	b.ie_transferencia 	= 'S';

	select	ie_transferencia
	into STRICT	ie_transferencia_w
	from	pat_tipo_historico
	where	nr_sequencia = nr_seq_tipo_p;

	if (ie_transferencia_w = 'S') and (nr_seq_ult_transf_w > 0) and (nr_seq_ult_transf_w <> nr_seq_hist_p) then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44358);
		end;
	end if;

	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
