CREATE OR REPLACE PROCEDURE consistir_pat_mes_ref ( dt_ref_p timestamp, cd_estabelecimento_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_consiste_estab_w		bigint;
ie_mes_w				timestamp;
ie_result_w			char;

BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	--Consistindo data vide mÃ©todo > Mes_calculado projeto: CorCtb_F6
	select	count(*)
	into STRICT	ie_consiste_estab_w
	from	pat_bem b,
		pat_valor_bem a
	where	a.nr_seq_bem		= b.nr_sequencia
	and	b.cd_estabelecimento	= cd_estabelecimento_p;

	--Sql se o sql acima tiver valor
	if (ie_consiste_estab_w IS NOT NULL AND ie_consiste_estab_w::text <> '') and (ie_consiste_estab_w != 0) then
		begin
		select	coalesce(max(a.dt_mes_ref), trunc(clock_timestamp(), 'mm'))
		into STRICT	ie_mes_w
		from	pat_mes_ref a
		where	a.ie_situacao		=   '1'
		and	a.cd_estabelecimento	=   cd_estabelecimento_p;

		if (dt_ref_p <= ie_mes_w) then
			begin
			ie_result_w := 'v';
			end;
		else
			begin
			ie_result_w := 'f';
			end;
		end if;
		end;
	end if;

	if (ie_result_w = 'v') then
		begin
		CALL wheb_mensagem_pck.exibir_mensagem_abort(44321);
		end;
	end if;

	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
