CREATE OR REPLACE PROCEDURE consistir_periodo_envelope ( cd_pessoa_fisica_p text, nr_sequencia_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
qt_registros_w		integer;
nm_pessoa_fisica_w	varchar(255);					

BEGIN 
 
select	count(*) 
into STRICT	qt_registros_w 
from	same_prontuario 
where	cd_pessoa_fisica		= cd_pessoa_fisica_p 
and	nr_sequencia			<> nr_sequencia_p 
and	((dt_periodo_inicial		between dt_inicial_p and dt_final_p) 
or (coalesce(dt_periodo_final,clock_timestamp())	between dt_inicial_p and dt_final_p));
 
if (qt_registros_w > 0) then 
	-- 'Já existe um prontuário para o paciente ' || obter_nome_pf(cd_pessoa_fisica_p) || ' no período informado. Favor verificar.#@#@' 
	NM_PESSOA_FISICA_W := obter_nome_pf(cd_pessoa_fisica_p);
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(263557,'NM_PESSOA_FISICA_W=' || NM_PESSOA_FISICA_W);
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
