CREATE OR REPLACE PROCEDURE consistir_peso_paciente_exame (cd_agenda_p bigint, qt_peso_paciente_p bigint, ie_peso_p INOUT text) AS $body$
DECLARE

 
ie_peso_w		varchar(1) := 'S';
qt_peso_aparelho_w	real;


BEGIN 
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (qt_peso_paciente_p IS NOT NULL AND qt_peso_paciente_p::text <> '') and (qt_peso_paciente_p > 0) then 
 
	/* obter peso aparelho */
 
	select	coalesce(max(qt_peso_max),0) 
	into STRICT	qt_peso_aparelho_w 
	from	agenda 
	where	cd_agenda = cd_agenda_p;
 
	/* consistir peso aparelho x paciente */
 
	if (qt_peso_aparelho_w > 0) and (qt_peso_paciente_p > qt_peso_aparelho_w) then 
 
		ie_peso_w := 'N';
 
	end if;
 
end if;
 
ie_peso_p := ie_peso_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
