SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_pf_executivo ( nr_sequencia_p text) AS $body$
DECLARE


qt_cpf_w		bigint;
ie_opcao_w		varchar(5);
cd_pessoa_fisica_w	varchar(10);
nr_cpf_w		varchar(20);
dt_real_w		timestamp;
nr_seq_empresa_w	bigint;
cd_empresa_w		bigint;


BEGIN

select 	max(nr_cpf),
	max(cd_empresa_ref)
into STRICT	nr_cpf_w,
	cd_empresa_w
from	checkup_imp_pessoa
where 	nr_sequencia = nr_sequencia_p;

select 	count(*)
into STRICT	qt_cpf_w
from	pessoa_fisica
where	somente_numero(nr_cpf)  = nr_cpf_w;

if (qt_cpf_w = 0) then
	ie_opcao_w := 'P';
elsif (qt_cpf_w > 1) then
	ie_opcao_w := 'P';
elsif (qt_cpf_w = 1) then
	ie_opcao_w := 'E';

	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	pessoa_fisica
	where	somente_numero(nr_cpf) = nr_cpf_w;

	update	checkup_imp_pessoa
	set	cd_pessoa_fisica = cd_pessoa_fisica_w
	where	nr_sequencia = nr_sequencia_p;

	select	max(dt_fim_real)
	into STRICT	dt_real_w
	from	checkup
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;

	if (dt_real_w IS NOT NULL AND dt_real_w::text <> '') then
		update	checkup_imp_pessoa
		set	dt_ultimo_checkup = dt_real_w
		where	nr_sequencia = nr_sequencia_p;
	end if;
end if;


update	checkup_imp_pessoa
set	ie_vinculo = ie_opcao_w
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_w = 'E') then

	select 	max(nr_sequencia)
	into STRICT	nr_seq_empresa_w
	from   	empresa_contrato_checkup
	where  	dt_final >= clock_timestamp()
	and    	cd_empresa = cd_empresa_w;

	if (nr_seq_empresa_w IS NOT NULL AND nr_seq_empresa_w::text <> '') and (nr_seq_empresa_w > 0) then

		insert into empresa_pessoa_checkup(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							cd_pessoa_fisica,
							nr_seq_contrato,
							qt_checkup_lib)
							values (nextval('empresa_pessoa_checkup_seq'),
							 clock_timestamp(),
							 wheb_usuario_pck.get_nm_usuario,
							 clock_timestamp(),
							 wheb_usuario_pck.get_nm_usuario,
							 cd_pessoa_fisica_w,
							 nr_seq_empresa_w,
							 1);

	end if;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_pf_executivo ( nr_sequencia_p text) FROM PUBLIC;

