SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_prazo_convenio_agend (cd_pessoa_fisica_p text, dt_agenda_p timestamp, cd_convenio_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE

	 
nr_seq_regra_w		bigint;
ds_mensagem_w		varchar(255);
qt_prazo_minimo_w	bigint;
qt_prazo_maximo_w	bigint;
dt_agenda_w		timestamp;
qt_dias_w		bigint;
	

BEGIN 
 
select	max(nr_sequencia) 
into STRICT	nr_seq_regra_w 
from	agenda_bloq_prazo_conv 
where	cd_convenio = cd_convenio_p;
 
if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then 
	select	max(ds_mensagem), 
		max(qt_prazo_minimo), 
		max(qt_prazo_maximo) 
	into STRICT	ds_mensagem_w, 
		qt_prazo_minimo_w, 
		qt_prazo_maximo_w 
	from	agenda_bloq_prazo_conv 
	where	nr_sequencia = nr_seq_regra_w;
	 
	select	max(b.dt_agenda) 
	into STRICT	dt_agenda_w 
	from	agenda a, 
		agenda_consulta b 
	where	a.cd_tipo_agenda	= 3 
	and	a.cd_agenda		= b.cd_agenda 
	and	b.ie_status_agenda	= 'E' 
	and	b.cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	b.dt_agenda < dt_agenda_p;
		 
	if (dt_agenda_w IS NOT NULL AND dt_agenda_w::text <> '') then		 
		qt_dias_w := trunc(dt_agenda_p) - trunc(dt_agenda_w);
		if (qt_dias_w > qt_prazo_minimo_w) and (qt_dias_w < qt_prazo_maximo_w) then 
			ds_mensagem_p	:= ds_mensagem_w;
		end if;				
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
