CREATE OR REPLACE PROCEDURE consistir_prescr_apos_alta (nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
qt_min_prescr_alta_w	bigint;
dt_prescr_alta_w	timestamp;


BEGIN 
 
select	coalesce(max(obter_valor_param_usuario(924, 43, obter_perfil_ativo, nm_usuario_p, 0)), 0) 
into STRICT	qt_min_prescr_alta_w
;
 
if (coalesce(qt_min_prescr_alta_w,0) <> 0) then	 
	if (coalesce(nr_atendimento_p,0) > 0) then 
	 
		begin 
		select	max(coalesce(dt_alta,clock_timestamp()) + qt_min_prescr_alta_w / 1440) 
		into STRICT	dt_prescr_alta_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_p;
		exception when others then 
			--O Parâmetro 43 da REP está informado com um valor muito grande! 
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(187646);
		end;
		 
		if (dt_prescr_alta_w < clock_timestamp()) then 
			--Você não tem permissão para fazer prescrições após a alta do paciente! Parâmetro 43. 
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(187645);
		end if;
	end if;
end if;
 
END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
