SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_prescr_conta ( nr_Atendimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

 
qt_conta_prov_w		bigint;
qt_conta_def_w		bigint;


BEGIN 
 
ds_erro_p	:= '';
 
select	count(*) 
into STRICT	qt_conta_prov_w 
from	conta_paciente 
where	nr_atendimento		= nr_atendimento_p 
and	ie_status_acerto	= 1;
			 
select	count(*) 
into STRICT	qt_conta_def_w 
from	conta_paciente 
where	nr_atendimento		= nr_atendimento_p 
and	ie_status_acerto	= 2;
				 
if (qt_conta_prov_w = 0) and (qt_conta_def_w > 0) then 
ds_erro_p := wheb_mensagem_pck.get_texto(178375);
end if;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
