CREATE OR REPLACE PROCEDURE consistir_prescr_diag_jp ( NR_SEQ_PRESCR_P PE_PRESCRICAO.NR_SEQUENCIA%TYPE, NR_SEQ_ORDENACAO_P pe_prescr_diag.NR_SEQ_ORDENACAO%TYPE, NR_PRIORIDADE_P pe_prescr_diag.NR_PRIORIDADE%TYPE ) AS $body$
BEGIN
    CALL consistir_nr_seq_ord_diag( NR_SEQ_PRESCR_P, NR_SEQ_ORDENACAO_P);
    UPDATE pe_prescr_diag SET
        NR_PRIORIDADE = NR_PRIORIDADE - 1 
    WHERE 
        nr_seq_prescr = NR_SEQ_PRESCR_P AND 
        NR_PRIORIDADE >= NR_PRIORIDADE_P;
    commit;
END;
