CREATE OR REPLACE PROCEDURE consistir_prescr_procedimento ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, nm_usuario_p text, cd_perfil_p bigint, ds_erro_p INOUT bigint) AS $body$
DECLARE


cd_estabelecimento_w		smallint;
dt_prescricao_w			timestamp;
nr_atendimento_w		bigint;
qt_peso_w			real;
qt_altura_cm_w			real;
nr_seq_exame_w			exame_laboratorio.nr_seq_exame%type;

/*	Erros
	0   - Não tem erro
	124 - Tem erro mas permite liberar a prescrição
	125 - Tem erro e não permite liberar a prescrição

	As consistências 125 devem ficar no final da procedure.
*/
BEGIN
begin
select	cd_estabelecimento,
	dt_prescricao,
	coalesce(nr_atendimento,0),
	qt_peso,
	qt_altura_cm
into STRICT	cd_estabelecimento_w,
	dt_prescricao_w,
	nr_atendimento_w,
	qt_peso_w,
	qt_altura_cm_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;
exception
	when others then
		cd_estabelecimento_w	:= 1;
		nr_atendimento_w	:= 0;
end;

CALL consistir_regra_prescr_proced(nr_prescricao_p, nr_seq_prescricao_p, cd_estabelecimento_w, cd_perfil_p, nm_usuario_p);

select	max(ie_erro)
into STRICT	ds_erro_p
from	prescr_procedimento
where	nr_prescricao	= nr_prescricao_p
and	((nr_sequencia		= nr_seq_prescricao_p) or (nr_seq_prescricao_p	= 0));


if (nr_seq_prescricao_p IS NOT NULL AND nr_seq_prescricao_p::text <> '') then

	select	max(a.nr_seq_exame)
	into STRICT	nr_seq_exame_w
	from 	prescr_procedimento a
	where	a.nr_prescricao = nr_prescricao_p
	and		a.nr_sequencia = nr_seq_prescricao_p;

	CALL gerar_exame_lab_dependente(nr_prescricao_p, nr_seq_prescricao_p, 10, nr_atendimento_w, nm_usuario_p, nr_seq_exame_w, null, null);
end if;


END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
