CREATE OR REPLACE PROCEDURE consistir_prim_hor_copia_rep (nr_prescr_copia_p text, dt_prescr_copia_p timestamp, hr_prim_hor_nova_p text, ie_dia_seguinte_p INOUT text, hr_menor_horario_p INOUT text) AS $body$
DECLARE


dt_sql_w		varchar(99);
dt_menor_horario_w	varchar(19);
hr_menor_horario_w	varchar(5);
ie_dia_seguinte_w	varchar(1) := 'N';
ds_sql_w		varchar(2000);
ds_sql_ww		varchar(2000);
qt_prescr_w		varchar(10);


BEGIN
if (nr_prescr_copia_p IS NOT NULL AND nr_prescr_copia_p::text <> '') and (dt_prescr_copia_p IS NOT NULL AND dt_prescr_copia_p::text <> '') and (hr_prim_hor_nova_p IS NOT NULL AND hr_prim_hor_nova_p::text <> '') then

	ds_sql_ww	:=
			'select	count(*)								' ||
			'from	prescr_medica									' ||
			'where	nr_prescricao in :nr_prescr_copia_p					' ||
			'and	not (sysdate between dt_inicio_prescr and dt_validade_prescr)           ' ||
			'and	dt_liberacao is not null                                                ';

	qt_prescr_w := obter_valor_dinamico_in_bv(ds_sql_ww, '', ':nr_prescr_copia_p', nr_prescr_copia_p, ',', qt_prescr_w);

	if (qt_prescr_w <> '0') then

		dt_sql_w	:=
				'to_date(' || chr(39) || to_char(dt_prescr_copia_p,'dd/mm/yyyy') || ' ' || hr_prim_hor_nova_p || ':00' || chr(39) || ',' ||
				chr(39) || 'dd/mm/yyyy hh24:mi:ss' || chr(39) || ')';

		ds_sql_w	:=
				'select	to_char(min(b.dt_horario),' || chr(39) || 'dd/mm/yyyy hh24:mi:ss' || chr(39) || ')	' ||
				'from	prescr_mat_hor b,							' ||
				'	prescr_material a							' ||
				'where	b.nr_prescricao = a.nr_prescricao					' ||
				'and	b.nr_seq_material = a.nr_sequencia					' ||
				'and	a.nr_prescricao in :nr_prescr_copia_p					' ||
				'and	b.dt_horario < ' || dt_sql_w || '					' ||
				'and	a.ie_agrupador = 1							' ||
				'and	b.ie_aprazado = '|| chr(39) || 'N' || chr(39) ||
				'and	nvl(b.ie_horario_especial,'|| chr(39) || 'N' || chr(39) || ') = '|| chr(39) || 'N' || chr(39) ||
				'and	nvl(a.ie_suspenso,' || chr(39) || 'N' || chr(39) || ') = ' || chr(39) || 'N' || chr(39);

		dt_menor_horario_w := obter_valor_dinamico_in_bv(ds_sql_w, '', ':nr_prescr_copia_p', nr_prescr_copia_p, ',', dt_menor_horario_w);
		hr_menor_horario_w	:= substr(dt_menor_horario_w,12,5);

		if (coalesce(hr_menor_horario_w::text, '') = '') or (hr_menor_horario_w = '') then

			ds_sql_w	:=
				'select	to_char(min(dt_inicio_prescr),' || chr(39) || 'dd/mm/yyyy hh24:mi:ss' || chr(39) || ')	' ||
				'from	prescr_medica							' ||
				'where	nr_prescricao in :nr_prescr_copia_p		';

			dt_menor_horario_w := obter_valor_dinamico_in_bv(ds_sql_w, '', ':nr_prescr_copia_p', nr_prescr_copia_p, ',', dt_menor_horario_w);

			hr_menor_horario_w	:= substr(dt_menor_horario_w,12,5);

		end if;

	else
		hr_menor_horario_w	:= to_char(trunc(clock_timestamp(), 'hh24') + 1/24, 'hh24:mi');
	end if;
end if;

ie_dia_seguinte_p	:= 'S';
hr_menor_horario_p	:= hr_menor_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
