SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_proced_cih (nr_atendimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


qt_sus_modalide_w	integer;
qt_sus_registro_w	integer;
ds_mensagem_w		varchar(2000) := null;
qt_idade_minima_w 	smallint;
qt_idade_maxima_w	smallint;
ie_sexo_w		varchar(1);
ie_sexo_pf_w		varchar(1);
idade_pf_w		integer;
cd_grupo_w		bigint;
cd_sub_grupo_w		bigint;

cd_interface_w		varchar(10);


BEGIN
cd_interface_w := Obter_param_Usuario(8009, 1, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, cd_interface_w);


/* Tratamento CIHA - Portaria 1171 */

If (coalesce(cd_interface_w, '2066') = '2066') then
	cd_grupo_w 	:= Sus_Obter_Estrut_Proc(cd_procedimento_p, ie_origem_proced_p, 'C', 'G');
	cd_sub_grupo_w 	:= Sus_Obter_Estrut_Proc(cd_procedimento_p, ie_origem_proced_p, 'C', 'S');

	If (cd_grupo_w not in (2,3,4,5)) and (cd_sub_grupo_w not in (101,701)) then
		ds_mensagem_w	:= WHEB_MENSAGEM_PCK.get_texto(280559,null);
	end if;
Else
	
	select 	count(*)
	into STRICT	qt_sus_modalide_w
	from	sus_procedimento_modal
	where	cd_procedimento =  cd_procedimento_p
	and	ie_origem_proced = ie_origem_proced_p
	and	cd_modalidade in (2,3,4);

	if (qt_sus_modalide_w = 0) then
		ds_mensagem_w	:= WHEB_MENSAGEM_PCK.get_texto(280560,null);
	end if;


	select 	count(*)
	into STRICT  	qt_sus_registro_w
	from	sus_procedimento_registro
	where	cd_procedimento =  cd_procedimento_p
	and	ie_origem_proced = ie_origem_proced_p
	and	cd_registro in (3,4);

	if (qt_sus_registro_w = 0) then
		ds_mensagem_w	:= WHEB_MENSAGEM_PCK.get_texto(280561,null);
	end if;

End if;
	
select 	coalesce(max(OBTER_SEXO_PF(cd_pessoa_fisica,'C')),'I'),
	coalesce(max(OBTER_IDADE_PF(cd_pessoa_fisica,coalesce(dt_alta,clock_timestamp()),'A')),0)
into STRICT	ie_sexo_pf_w,
	idade_pf_w
from	atendimento_paciente
where 	nr_atendimento = nr_atendimento_p;

select	max(Sus_Converte_Idade_Anos(qt_idade_minima,'C')),
	max(Sus_Converte_Idade_Anos(qt_idade_maxima,'C')),
	max(ie_sexo)
into STRICT	qt_idade_minima_w,
	qt_idade_maxima_w,
	ie_sexo_w
from	sus_procedimento
where	cd_procedimento =  cd_procedimento_p
and	ie_origem_proced = ie_origem_proced_p;

if  	((qt_idade_minima_w IS NOT NULL AND qt_idade_minima_w::text <> '') and (qt_idade_maxima_w IS NOT NULL AND qt_idade_maxima_w::text <> '') and
	((idade_pf_w < qt_idade_minima_w) or (idade_pf_w > qt_idade_maxima_w))) then
	ds_mensagem_w	:= WHEB_MENSAGEM_PCK.get_texto(280562,null);
end if;

if 	(ie_sexo_w <> 'I' AND ie_sexo_pf_w <> ie_sexo_w) then
	ds_mensagem_w	:= WHEB_MENSAGEM_PCK.get_texto(280564,null);
end if;

ds_erro_p	:= ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_proced_cih (nr_atendimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ds_erro_p INOUT text) FROM PUBLIC;

