SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_prod_leites_deriv ( nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_leite_deriv_p bigint, nm_usuario_p text, cd_perfil_p bigint, ds_erro_p INOUT bigint) AS $body$
DECLARE

					
nr_seq_material_w 		bigint;
ds_erro_w				smallint;
nr_prescricao_w			bigint;
dt_inicio_prescr_w		timestamp;
dt_inicio_item_w		timestamp;
hr_prim_horario_w		varchar(10);
nr_seq_material_ww		bigint;
nr_atendimento_w		bigint;
dt_maior_w				timestamp;
dt_menor_w				timestamp;
dt_primeiro_horario_w	prescr_medica.dt_primeiro_horario%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	prescr_material a
	where	a.nr_prescricao		= nr_prescricao_p
	and	(a.nr_seq_leite_deriv IS NOT NULL AND a.nr_seq_leite_deriv::text <> '')
	and	coalesce(a.dt_liberacao::text, '') = ''
	and	a.ie_agrupador	= 16
	order by 1;
	
C02 CURSOR FOR
	SELECT	a.nr_prescricao,
		b.nr_sequencia
	from	prescr_material b,
		prescr_medica a                    
	where	a.nr_atendimento = nr_atendimento_w
	and	a.nr_prescricao = b.nr_prescricao
	and	b.nr_prescricao <> nr_prescricao_p
	and	a.dt_prescricao between clock_timestamp() - interval '5 days' and clock_timestamp() + interval '1 days'
	and	coalesce(a.dt_suspensao::text, '') = ''
	and	((a.nm_usuario_original = nm_usuario_p) and (not((coalesce(a.dt_liberacao,a.dt_liberacao_medico) IS NOT NULL AND (coalesce(a.dt_liberacao,a.dt_liberacao_medico))::text <> ''))))
	and	b.ie_agrupador = 16
	and	coalesce(b.dt_suspensao::text, '') = ''
	and	coalesce(b.ie_suspenso,'N') = 'N'
	and	(obter_classif_mat_leite_deriv(b.cd_material) IS NOT NULL AND (obter_classif_mat_leite_deriv(b.cd_material))::text <> '')
	order by 1;


BEGIN

select	MAX(nr_atendimento),
		MAX(dt_inicio_prescr),
		max(dt_primeiro_horario)
into STRICT	nr_atendimento_w,
		dt_inicio_prescr_w,
		dt_primeiro_horario_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

select	max(hr_prim_horario)
into STRICT	hr_prim_horario_w
from	prescr_leite_deriv
where	nr_prescricao	= nr_prescricao_p
and		nr_sequencia	= nr_seq_leite_deriv_p;

if (coalesce(hr_prim_horario_w::text, '') = '') or (trim(both hr_prim_horario_w) = ':') then
	hr_prim_horario_w	:= to_char(dt_primeiro_horario_w, 'hh24:mi:ss');
end if;

dt_inicio_item_w	:= trunc(ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_inicio_prescr_w, hr_prim_horario_w),'mi');

open C01;
loop
fetch C01 into	
	nr_seq_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_erro_w := consistir_prescr_material(nr_prescricao_p, nr_seq_material_w, nm_usuario_p, cd_perfil_p, ds_erro_w);
	
	if (nr_seq_material_w = nr_seq_material_p) then
		ds_erro_p	:= ds_erro_w;
	end if;

	open C02;
	loop
	fetch C02 into	
		nr_prescricao_w,
		nr_seq_material_ww;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		dt_maior_w	:= obter_data_leite_deriv(nr_prescricao_w, 'M');
		dt_menor_w	:= obter_data_leite_deriv(nr_prescricao_w, 'm');
		
		if (dt_inicio_item_w between dt_menor_w and dt_maior_w) then

			ds_erro_w := consistir_prescr_material(nr_prescricao_w, nr_seq_material_ww, nm_usuario_p, cd_perfil_p, ds_erro_w);			
			
		end if;
		
		end;
	end loop;
	close C02;
	
	end;
end loop;
close C01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_prod_leites_deriv ( nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_leite_deriv_p bigint, nm_usuario_p text, cd_perfil_p bigint, ds_erro_p INOUT bigint) FROM PUBLIC;

