CREATE OR REPLACE PROCEDURE consistir_prog_entrega_ressup ( cd_estabelecimento_p bigint, cd_material_p bigint, dt_entrega_solicitada_p timestamp, nr_Sequencia_p bigint) AS $body$
DECLARE


qt_existe_w		bigint;


BEGIN

if (trunc(dt_entrega_solicitada_p,'dd') < trunc(clock_timestamp(),'dd')) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265961);
	--'A data informada é menor que a data atual'
end if;

select	count(*)
into STRICT	qt_existe_w
from	prog_entregra_mat_ressup
where	cd_estabelecimento = cd_estabelecimento_p
and	cd_material = cd_material_p
and	trunc(dt_entrega_solicitada,'dd') = trunc(dt_entrega_solicitada_p,'dd')
and	nr_sequencia <> nr_sequencia_p;

if (qt_existe_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265962);
	--'Já existe uma entrega deste material para esta data.'
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
