SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_qt_aprazamento ( cd_perfil_p bigint, cd_item_p bigint, nr_prescricao_p bigint, nr_seq_item_p bigint) AS $body$
DECLARE


qt_w		bigint;
qt_maxima_w	bigint;
ie_existe_regra_w	varchar(1);
			

BEGIN

select	coalesce(max(qt_maxima),0),
		coalesce(max('S'), 'N')
into STRICT	qt_maxima_w,
		ie_existe_regra_w
from	regra_quant_aprazamento
where	((coalesce(cd_perfil::text, '') = '') or (cd_perfil = cd_perfil_p))
and		cd_material	= cd_item_p;

if (ie_existe_regra_w = 'S') then
	select	count(*)
	into STRICT	qt_w
	from	prescr_mat_hor
	where	nr_prescricao	= nr_prescricao_p
	and		nr_seq_material	= nr_seq_item_p
	and		coalesce(ie_horario_especial,'N') = 'N';
	
	if (qt_w >= qt_maxima_w) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(218875,'QTD='||to_char(qt_maxima_w));
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
