SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_qt_devol_mat_conta (nr_atendimento_p bigint, nr_seq_atepacu_p bigint, cd_material_p bigint, qt_devolvido_p bigint, cd_setor_atendimento_p bigint, ie_consiste_exec_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_saldo_w	double precision;


BEGIN

ds_retorno_p	:= '';

select	sum(qt_material)
into STRICT	qt_saldo_w
from	material_atend_paciente
where 	nr_atendimento		= nr_atendimento_p
and	CASE WHEN ie_consiste_exec_p='I' THEN  coalesce(cd_material_exec, cd_material)  ELSE cd_material END  = cd_material_p
and	cd_setor_atendimento	= cd_setor_atendimento_p;
/*and	nr_seq_atepacu		= nr_seq_atepacu_p; Felipe - OS53122 - Estava consistindo pela passagem e não pelo setor */

if	((qt_devolvido_p  + qt_saldo_w) < 0) then
	ds_retorno_p	:=  WHEB_MENSAGEM_PCK.get_texto(304918); --'A devolução é maior que o saldo do material!';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
