CREATE OR REPLACE PROCEDURE consistir_queda_gravidade (nr_seq_ficha_p bigint) AS $body$
DECLARE


ie_gravidade_w varchar(1);


BEGIN

if (nr_seq_ficha_p IS NOT NULL AND nr_seq_ficha_p::text <> '') and (nr_seq_ficha_p > 0)	then

	select	coalesce(max(a.ie_gravidade),0)
	into STRICT	ie_gravidade_w
    from	queda_consequencia a,
			queda_ficha_consequencia b
	where	b.nr_seq_consequencia = a.nr_sequencia
	and		b.nr_seq_ficha = nr_seq_ficha_p;


	update	Queda_ficha_ocorrencia
	set		ie_gravidade = ie_gravidade_w
	where	nr_sequencia = nr_seq_ficha_p;

	commit;


end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
