CREATE OR REPLACE PROCEDURE consistir_receita_farm_item ( nr_atendimento_p bigint, cd_material_p text, cd_medico_p text, ds_valida_dt_medicamento_p text, cd_intervalo_p text, dt_inicio_p timestamp, dt_inicio_item_p timestamp, qt_operacao_p INOUT bigint, ds_texto_receita_p INOUT text, ds_mensagem_p INOUT text, nr_seq_item_p bigint default null, ie_abortar_p INOUT text DEFAULT NULL) AS $body$
DECLARE


ds_mensagem_w		varchar(255);
ds_texto_receita_w	varchar(2000);
ds_exibe_mat_w		varchar(1);
ds_possui_receita_w	varchar(1);
vl_valida_intervalo_w	 integer;
qt_operacao_w		 integer;
ie_param_40_w		varchar(1);
ie_abortar_w		varchar(1);
ie_situacao_w     	varchar(1);
ie_existe_w    		varchar(1);


BEGIN
	ie_abortar_w	:= 'N';

	if (obtain_user_locale(wheb_usuario_pck.get_nm_usuario) <> 'en_AU') then
		ie_param_40_w := obter_param_usuario(10015, 40, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_param_40_w);
		ds_exibe_mat_w 	:= fa_obter_se_exibe_mat(cd_material_p,cd_medico_p);

		select coalesce(max(a.ie_situacao), 'A')
		into STRICT   ie_situacao_w
		from   fa_medic_farmacia_amb a
		where  a.cd_material = cd_material_p;

		select CASE WHEN coalesce(max(nr_sequencia)::text, '') = '' THEN 'N'  ELSE 'S' END
		into STRICT   ie_existe_w
		from   fa_medic_farmacia_amb a
		where  a.cd_material = cd_material_p
		and    coalesce(a.cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento;

		if (ds_exibe_mat_w = 'N') then
			  CALL Wheb_mensagem_pck.exibir_mensagem_abort(33139);
		elsif (ie_situacao_w = 'I') then
			  CALL Wheb_mensagem_pck.exibir_mensagem_abort(496723);
			elsif (ie_existe_w = 'N') then
				  CALL Wheb_mensagem_pck.exibir_mensagem_abort(496724);
		end if;

		ds_possui_receita_w := fa_obter_se_possui_receita(cd_material_p,null,nr_atendimento_p,dt_inicio_p,nr_seq_item_p);

		if (ds_possui_receita_w = 'S') then
			if (ie_param_40_w = 'N') then
				CALL Wheb_mensagem_pck.exibir_mensagem_abort(33143);
			elsif (ie_param_40_w = 'A') then
				ds_mensagem_w 	:= substr(obter_texto_tasy(33143, wheb_usuario_pck.get_nr_seq_idioma),1,255);
				ie_abortar_w	:= 'N';
			elsif (ie_param_40_w = 'C') then
				ds_mensagem_w 	:= substr(obter_texto_tasy(245315, wheb_usuario_pck.get_nr_seq_idioma),1,255);
				ie_abortar_w	:= 'N';
			end if;
		end if;
	end if;

	if (ds_valida_dt_medicamento_p = 'N') then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(33146);
	end if;


	if (dt_inicio_item_p IS NOT NULL AND dt_inicio_item_p::text <> '') then
	begin

		if (trunc(dt_inicio_item_p) < trunc(dt_inicio_p)) then
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(33147);
		end if;
	end;
	end if;

	/*if	(cd_intervalo_p is null) then
		Wheb_mensagem_pck.exibir_mensagem_abort(33148);
	else
		begin

		select	nvl(max(qt_operacao),0)
		into	qt_operacao_w
		from	intervalo_prescricao
		where	cd_intervalo = cd_intervalo_p
		and	ie_operacao = 'H';

		ds_mensagem_w := substr(obter_texto_tasy (33188, wheb_usuario_pck.get_nr_seq_idioma),1,255);

		end;
	end if;	*/
	select 	max(ds_texto_padrao_receita) ds_texto_padrao_receita
	into STRICT	ds_texto_receita_w
	from	fa_medic_farmacia_amb
	where	cd_material = cd_material_p
	and		cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

qt_operacao_p		:= qt_operacao_w;
ds_texto_receita_p	:= ds_texto_receita_w;
ds_mensagem_p		:= ds_mensagem_w;
ie_abortar_p		:= ie_abortar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
