SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_regra_adic_hora (cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, cd_convenio_p bigint, cd_estabelecimento_p bigint, nr_seq_proc_p bigint, nr_atendimento_p bigint, dt_procedimento_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
ie_existe_regra_w	varchar(1) := 'N';
--qt_proc_mesmo_cod_w	number(10) := 0; 
dt_primeiro_proc_w	timestamp;


BEGIN 
 
begin 
select	'S' 
into STRICT	ie_existe_regra_w 
from	regra_consiste_adic_hora 
where	coalesce(cd_convenio,cd_convenio_p) 			= cd_convenio_p 
and	cd_estabelecimento 				= cd_estabelecimento_p 
and	coalesce(cd_procedimento,cd_procedimento_p) 		= cd_procedimento_p 
and	((coalesce(cd_procedimento::text, '') = '') or 
	coalesce(ie_origem_proced,ie_origem_proced_p) 	= ie_origem_proced_p) 
and	coalesce(nr_seq_proc_interno,nr_seq_proc_interno_p) 	= nr_seq_proc_interno_p  LIMIT 1;
exception 
when others then 
	ie_existe_regra_w := 'N';
end;
 
 
 
if (coalesce(ie_existe_regra_w,'N') = 'S') then 
 
	select	min(a.dt_procedimento) 
	into STRICT	dt_primeiro_proc_w 
	from	procedimento_paciente a, 
		conta_paciente b 
	where	a.nr_interno_conta 	= b.nr_interno_conta 
	and	a.cd_procedimento 	= cd_procedimento_p 
	and	a.ie_origem_proced 	= ie_origem_proced_p 
	and	a.nr_sequencia		< nr_seq_proc_p 
	and	a.cd_convenio		= cd_convenio_p 
	and	b.cd_estabelecimento 	= cd_estabelecimento_p 
	and	b.nr_atendimento	= nr_atendimento_p 
	and	a.dt_procedimento	< dt_procedimento_p 
	and	b.ie_status_acerto	= '1';
 
	if (dt_primeiro_proc_w IS NOT NULL AND dt_primeiro_proc_w::text <> '') then 
 
		CALL Dispensa_cobranca_adic_hor(nr_seq_proc_p,null,'CONSISTIR_REGRA_ADIC_HORA','N',nm_usuario_p);
 
	end if;
 
end if;
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
