SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_regra_alerta_js ( ie_tipo_convenio_p bigint, ie_tipo_atendimento_p bigint, cd_pessoa_fisica_p text, ds_msg_p INOUT text) AS $body$
DECLARE

 
qt_horas_ultimo_atend_w	integer;
ie_tipo_atendimento_w	varchar(255);
ie_tipo_convenio_w	varchar(255);
dt_entrada_w		timestamp;
nr_atendimento_w	bigint;
dt_alta_w		timestamp;
ds_msg_w		varchar(400);
dt_alta_str_w		varchar(60);
				

BEGIN 
 
if (coalesce(ie_tipo_convenio_p,0) > 0) and (coalesce(ie_tipo_atendimento_p,0) > 0) and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	begin 
	 
	select 	coalesce(max(qt_horas_ultimo_atend), 0) 
	into STRICT	qt_horas_ultimo_atend_w	 
	from  	regra_alerta_gerar_atend 
	where 	ie_tipo_convenio = ie_tipo_convenio_p 
	and  	ie_tipo_atendimento = ie_tipo_atendimento_p;
	 
	if (qt_horas_ultimo_atend_w > 0)then 
		begin 
		 
		select 	max(dt_entrada), 
			max(nr_atendimento), 
			max(dt_alta) 
		into STRICT	dt_entrada_w, 
			nr_atendimento_w, 
			dt_alta_w 
		from  	atendimento_paciente 
		where 	cd_pessoa_fisica = cd_pessoa_fisica_p 
		and  	ie_tipo_atendimento = ie_tipo_atendimento_p 
		and  	ie_tipo_convenio = ie_tipo_convenio_p 
		and (clock_timestamp() - dt_entrada) <= (qt_horas_ultimo_atend_w / 24) 
		order by dt_entrada desc;
		 
		end;
		 
		if (coalesce(nr_atendimento_w,0) > 0) then 
		 
			ie_tipo_atendimento_w	:= obter_valor_dominio(12, ie_tipo_atendimento_p);
			ie_tipo_convenio_w	:= obter_valor_dominio(11, ie_tipo_convenio_p);
			 
			if (coalesce(dt_alta_w::text, '') = '') then 
				dt_alta_str_w := ' ';
			else 
				dt_alta_str_w := to_char(dt_alta_w,'dd/mm/yyyy hh24:mi:ss');
			end if;
			 
			ds_msg_w := substr(obter_texto_dic_objeto(93280, wheb_usuario_pck.get_nr_seq_idioma, 'IE_TIPO_ATENDIMENTO_W='||ie_tipo_atendimento_w||';IE_TIPO_CONVENIO_W='||ie_tipo_convenio_w|| 
			';QT_HORAS_ULTIMO_ATEND_W='||qt_horas_ultimo_atend_w||';DT_ENTRADA_W='||to_char(dt_entrada_w,'dd/mm/yyyy hh24:mi:ss')||';NR_ATENDIMENTO_W='||nr_atendimento_w||';DT_ALTA_W='|| dt_alta_str_w),1,400);
		 
		end if;
	end if;
 
	end;
end if;
 
ds_msg_p := ds_msg_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_regra_alerta_js ( ie_tipo_convenio_p bigint, ie_tipo_atendimento_p bigint, cd_pessoa_fisica_p text, ds_msg_p INOUT text) FROM PUBLIC;

