-- DATASOURCE: dbi:Oracle:host=srv-dbora-03.whebdc.com.br;service_name=DEV_1815;port=1521

SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_regra_prot_medic ( nr_seq_paciente_p bigint, nr_seq_material_p bigint, nm_usuario_p text, nr_seq_regra_p bigint default 0, nr_ciclo_p bigint default 0, ie_forma_consistencia_p text default 'X', cd_material_p bigint default null, cd_unid_med_prescr_p text default null, qt_dose_prescr_p bigint default null, ie_via_aplicacao_p text default null, ds_observacao_p text default null, ds_ciclos_aplicacao_p text default null, is_paciente_medic_update text default 'N', qt_dias_util_p bigint default null, ie_local_adm_p text default null, ie_uso_continuo_p text default null, cd_intervalo_p text default null, qt_dose_p bigint default null, ds_justificativa_p bigint default null, ds_dose_diferenciada_p text default null, cd_unid_med_dose_p text default null) AS $body$
DECLARE

						
ds_erro_w		varchar(300);
ds_erro2_w		varchar(300);
cd_perfil_w		bigint;
nr_seq_erro_w		bigint;
ds_obs_w		varchar(4000);
cd_material_w		bigint;
ie_padronizado_w		varchar(2);
ie_padronizado_lib_w 	varchar(2);
ie_uso_continuo_w		varchar(1);
ie_local_adm_w		varchar(1);	
qt_dias_util_w		smallint;
cd_pessoa_fisica_w 	varchar(10);
ie_regra_2_w 		varchar(2) := 'N';
ie_regra_10_w 		varchar(2) := 'N';
ie_regra_12_w 		varchar(2) := 'N';
ie_regra_14_w 		varchar(2) := 'N';

cd_convenio_w  		bigint;
cd_plano_w 		varchar(10);	
nr_atendimento_w 		bigint;	
cd_setor_atendimento_w 	bigint;
nr_seq_regra_plano_w	bigint;
qt_dose_prescricao_w  	double precision;
ie_regra_w		varchar(10);
ds_justificativa_w		varchar(1);
ie_medic_padronizado_w	varchar(2);
qt_creatinina_w		real;
ie_paciente_alergico_w  	varchar(1);
ds_item_w               		varchar(100);
ie_ciclo_lib_w		varchar(1) := 'N';
ds_mensagem_p 		varchar(200) := null;
cd_intervalo_w		varchar(7);
cd_intervalo_solucao_w	varchar(7);
nr_seq_diluicao_w       	integer;
qt_saldo_disponivel_estoque_w double precision;
ie_material_disponivel_w 	 varchar(1);
nr_seq_proc_interno_w      bigint;
nr_seq_atendimento_w	paciente_atendimento.nr_seq_atendimento%type;


BEGIN

cd_perfil_w := obter_perfil_ativo;

delete from paciente_atendimento_erro
where nr_seq_paciente = nr_seq_paciente_p
and nr_seq_material = nr_seq_material_p;

select 	coalesce(max(nr_seq_atendimento), 0)
into STRICT	nr_seq_atendimento_w
from 	paciente_atendimento
where  	nr_seq_paciente = nr_seq_paciente_p;

delete from paciente_atendimento_erro
where	nr_seq_atendimento = nr_seq_atendimento_w
and	nr_seq_material = nr_seq_material_p;
	
select	max(ds_observacao),
	max(cd_material),
	max(ie_uso_continuo),
	max(ie_local_adm),
	max(qt_dias_util),
	max(cd_intervalo),
	max(nr_seq_diluicao)
into STRICT	ds_obs_w,
	cd_material_w,
	ie_uso_continuo_w,
	ie_local_adm_w,
	qt_dias_util_w,
	cd_intervalo_w,
	nr_seq_diluicao_w
from (
SELECT	a.ds_observacao,
	a.cd_material,
	a.ie_uso_continuo,
	a.ie_local_adm,
	a.qt_dias_util,
	a.cd_intervalo,
	a.nr_seq_diluicao,
	a.nr_seq_solucao
from	paciente_protocolo_medic a
where 	a.nr_seq_material = nr_seq_material_p
and 	a.nr_seq_paciente = nr_seq_paciente_p
and   	coalesce(a.nr_seq_solucao::text, '') = ''

union

SELECT	a.ds_observacao,
	a.cd_material,
	a.ie_uso_continuo,
	a.ie_local_adm,
	a.qt_dias_util,
	b.cd_intervalo,
	a.nr_seq_diluicao,
	a.nr_seq_solucao
from 	paciente_protocolo_medic a,
	paciente_protocolo_soluc b
where 	a.nr_seq_material = nr_seq_material_p
and 	a.nr_seq_paciente = nr_seq_paciente_p
and 	a.nr_seq_solucao = b.nr_seq_solucao
and 	a.nr_seq_paciente = b.nr_seq_paciente
and 	(a.nr_seq_solucao IS NOT NULL AND a.nr_seq_solucao::text <> '')) x;

if (is_paciente_medic_update = 'S') then

	ds_obs_w := ds_observacao_p;
	cd_material_w := cd_material_p;
	ie_uso_continuo_w := ie_uso_continuo_p;
	ie_local_adm_w := ie_local_adm_p;
	qt_dias_util_w := qt_dias_util_p;
	cd_intervalo_w := cd_intervalo_p;

end if;

select  max(cd_pessoa_fisica),
	max(OBTER_ATENDIMENTO_PACIENTE_qt(cd_pessoa_fisica,wheb_usuario_pck.get_cd_estabelecimento))
into STRICT	cd_pessoa_fisica_w,
	nr_atendimento_w
from	paciente_Setor
where	nr_seq_paciente = nr_seq_paciente_p;


if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 1)) and (obter_se_consistir_regra_onc(1,cd_perfil_w)	= 'S') then


	SELECT * FROM Verifica_dose_maxima_prot_onc(nr_seq_paciente_p, nr_seq_material_p, 'N', ds_erro_w, ds_erro2_w, ds_mensagem_p, cd_material_p, cd_unid_med_prescr_p, qt_dose_prescr_p, ie_via_aplicacao_p, ds_observacao_p, ds_ciclos_aplicacao_p, is_paciente_medic_update, qt_dias_util_p, ie_local_adm_p, ie_uso_continuo_p, cd_intervalo_p, qt_dose_p, ds_justificativa_p, cd_unid_med_dose_p) INTO STRICT ds_erro_w, ds_erro2_w, ds_mensagem_p;
	

	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '')  or (ds_erro2_w IS NOT NULL AND ds_erro2_w::text <> '')then
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 1, ds_erro_w||ds_erro2_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w, ds_mensagem_p);
	end if;

end if;

if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 2)) then
	ie_regra_2_w := obter_se_consistir_regra_onc(2,cd_perfil_w);
end if;

if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 10)) then
	ie_regra_10_w := obter_se_consistir_regra_onc(10,cd_perfil_w);
end if;

if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 10)) then
	ie_regra_12_w := obter_se_consistir_regra_onc(12,cd_perfil_w);
END IF;

if (ie_regra_2_w	= 'S') or (ie_regra_10_w	= 'S') or (ie_regra_12_w	= 'S') then	
	
	ie_padronizado_w	:=  substr(obter_se_material_padronizado(wheb_usuario_pck.get_cd_estabelecimento,cd_material_w),1,2);	
	
	if (cd_pessoa_fisica_w > 0) then
		ie_padronizado_lib_w := substr(Obter_se_medic_lib_pac_onc(cd_pessoa_fisica_w,cd_material_w,nr_seq_paciente_p,nr_seq_material_p,'P'),1,2);
		ie_medic_padronizado_w := substr(obter_se_medic_pac_onc(cd_pessoa_fisica_w,cd_material_w),1,2); --function nova
	end if;
	
	if   	((ie_regra_10_w	= 'S') and (ie_padronizado_w = 'N') and (coalesce(ie_medic_padronizado_w,'S') = 'N')) then
		
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 10, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
	
	elsif (ie_regra_12_w	= 'S') and (ie_padronizado_w = 'N') and (coalesce(ie_padronizado_lib_w,'S') = 'N') then
	
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 12, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
	
	elsif (ie_regra_2_w	= 'S') and (coalesce(ds_obs_w::text, '') = '') and (ie_padronizado_w = 'N') then
	
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 2, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
	end if;
end if;

if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 4)) and (obter_se_consistir_regra_onc(4,cd_perfil_w) = 'S') then

	SELECT * FROM Verifica_dose_prot_minima_onc(nr_seq_paciente_p, nr_seq_material_p, 'N', is_paciente_medic_update, cd_material_p, cd_unid_med_prescr_p, qt_dose_prescr_p, ie_via_aplicacao_p, ds_justificativa_p, ds_erro_w, ds_erro2_w
  ) INTO STRICT ds_erro_w, ds_erro2_w
;
	
	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') or (ds_erro2_w IS NOT NULL AND ds_erro2_w::text <> '')then
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 4, ds_erro_w||ds_erro2_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
	end if;
end if;

if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 5)) and (obter_se_consistir_regra_onc(5,cd_perfil_w)	= 'S') then

    Select 	max(nr_seq_proc_interno)
	into STRICT	nr_seq_proc_interno_w
	from 	paciente_protocolo_proc
	where	nr_seq_paciente = nr_seq_paciente_p;

	if (coalesce(nr_seq_proc_interno_w::text, '') = '') then
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 5, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
	end if;
end if;

if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 6)) and (obter_se_consistir_regra_onc(6,cd_perfil_w)	= 'S') then
	
	SELECT * FROM Verifica_dose_prescr_prot_onc(nr_seq_paciente_p, nr_seq_material_p, ds_erro_w, ds_erro2_w) INTO STRICT ds_erro_w, ds_erro2_w;
	
	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '')  or (ds_erro2_w IS NOT NULL AND ds_erro2_w::text <> '')then	
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 6, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);		
	end if;
	
end if;

if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 7)) and (obter_se_consistir_regra_onc(7,cd_perfil_w)	= 'S') then
	if (ie_uso_continuo_w = 'S') and (ie_local_adm_w = 'C') and (coalesce(qt_dias_util_w::text, '') = '') then
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 7, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
	end if;
end if;

if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 14)) and (obter_se_consistir_regra_onc(14,cd_perfil_w) = 'S') then

	select	max(verificar_dose_dif_onc(nr_seq_paciente_p,0,nr_seq_material_p, ds_dose_diferenciada_p, cd_intervalo_w))
	into STRICT	ds_erro_w
	;

	if ((trim(both ds_erro_w) IS NOT NULL AND (trim(both ds_erro_w))::text <> '')) then
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 14, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
	end if;
end if;

--Antonio
begin
	cd_setor_atendimento_w := obter_setor_atendimento(nr_atendimento_w);
exception
when others then
	cd_setor_atendimento_w := 0;
end;

SELECT	MAX(QT_DOSE_PRESCR)
INTO STRICT	qt_dose_prescricao_w
FROM	paciente_protocolo_medic
WHERE	NR_SEQ_PACIENTE = nr_seq_paciente_p
AND	nr_seq_material = nr_seq_material_p;

if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '')then
	select obter_convenio_atendimento(nr_atendimento_w),
		obter_plano_atendimento(nr_atendimento_w,'C')
	into STRICT cd_convenio_w,cd_plano_w
	;
end if;


if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 8)) and (obter_se_consistir_regra_onc(8,cd_perfil_w)	= 'S' ) and (cd_convenio_w IS NOT NULL AND cd_convenio_w::text <> '') then
	
	
	SELECT * FROM consiste_mat_plano_convenio(	coalesce(cd_convenio_w,0), cd_plano_w, cd_material_w, nr_atendimento_w, cd_setor_atendimento_w, ds_erro_w, ds_erro2_w, ie_regra_w, nr_seq_regra_plano_w, qt_dose_prescricao_w, clock_timestamp(), null, wheb_usuario_pck.get_cd_estabelecimento, null, null) INTO STRICT ds_erro_w, ds_erro2_w, ie_regra_w, nr_seq_regra_plano_w;
	if (ie_regra_w = 6) then
		if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '')  or (ds_erro2_w IS NOT NULL AND ds_erro2_w::text <> '')then
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 8, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
		end if;
		
	end if;
	
end if;
--Antonio
if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 11)) and (obter_se_consistir_regra_onc(11,cd_perfil_w)	= 'S') then
	
	ds_justificativa_w := obter_se_regra_just_prot_onc(11, nr_seq_paciente_p, nr_seq_material_p, ds_justificativa_w);
	
	if (ds_justificativa_w = 'N') then

		ds_erro_w := obter_concent_prot_onc(nr_seq_paciente_p, nr_seq_material_p,'M');

		if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
			nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 11, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
		end if;
	
	end if;
		
end if;



if	((nr_seq_regra_p = 0) or (nr_seq_regra_p = 13)) and (obter_se_consistir_regra_onc(13,cd_perfil_w)	= 'S') then

	Select 	max(QT_CREATININA)
	into STRICT	qt_creatinina_w
	from 	PACIENTE_SETOR
	where	nr_seq_paciente = nr_seq_paciente_p;	
	
	if (coalesce(qt_creatinina_w::text, '') = '') then
	
		ds_justificativa_w := obter_se_regra_just_prot_onc(13, nr_seq_paciente_p, nr_seq_material_p, ds_justificativa_w);
		
		if (ds_justificativa_w = 'N') then

			ds_erro_w := obter_consiste_creatinina(nr_seq_paciente_p,nr_seq_material_p,'P');

			if (ds_erro_w	= 'S') then
				nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 13, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
			end if;
		
		end if;
	
	end if;
		
end if;

if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 15)) and (obter_se_consistir_regra_onc(15,cd_perfil_w) = 'S')  then

     cd_pessoa_fisica_w := obter_pessoa_atendimento(nr_atendimento_w,'C');
     SELECT * FROM Verifica_Paciente_Alergico(cd_pessoa_fisica_w, cd_material_w, ds_item_w, ie_paciente_alergico_w) INTO STRICT ds_item_w, ie_paciente_alergico_w;

     if (ie_paciente_alergico_w = 'S') then
          nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 15, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
     end if;
end if;

if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 16)) and (obter_se_consistir_regra_onc(16,cd_perfil_w) = 'S') then
	
    CALL consistir_regra_prescr_onc(nr_seq_paciente_p, nr_seq_material_p, 'P', 16);
     
end if;


if  ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 17)) and (obter_se_consistir_regra_onc(17,cd_perfil_w) = 'S') then
	
	select	coalesce(max('S'),'N')
	into STRICT	ie_ciclo_lib_w
	from	paciente_setor_lib
	where	nr_seq_paciente	= nr_seq_paciente_p
	and	coalesce(dt_cancelamento::text, '') = ''
	and	((nr_ciclo = nr_ciclo_p) or (nr_ciclo_p = 0));	
	
	
	if (coalesce(ie_ciclo_lib_w,'N') = 'N') then
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 17, substr(ds_erro_w,1,255), cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
	end if;
end if;

if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 18)) and (obter_se_consistir_regra_onc(18,cd_perfil_w)	= 'S') then
	
	if ((coalesce(cd_intervalo_w::text, '') = '') or (obter_se_intervalo_ativo(cd_intervalo_w) = 'I')) and (nr_seq_diluicao_w = 0 or coalesce(nr_seq_diluicao_w::text, '') = '') then
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 18, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
	end if;
end if;

if ((nr_seq_regra_p = 0) or (nr_seq_regra_p = 19)) and (obter_se_consistir_regra_onc(19,cd_perfil_w)	= 'S') then

  	select coalesce(ie_disponivel_mercado,'N')
  	into STRICT ie_material_disponivel_w
  	FROM material
  	WHERE cd_material = cd_material_w;

  	qt_saldo_disponivel_estoque_w := 0;

	if (ie_material_disponivel_w = 'E') then
	 	qt_saldo_disponivel_estoque_w := obter_saldo_disp_estoque(wheb_usuario_pck.get_cd_estabelecimento, cd_material_w, 0, clock_timestamp());
	end if;

	if (ie_material_disponivel_w <> 'S') and (qt_saldo_disponivel_estoque_w <= 0) THEN
      		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 19, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
	end if;
end if;

if (((nr_seq_regra_p = 0) or (nr_seq_regra_p = 20)) and (obter_se_consistir_regra_onc(20, cd_perfil_w) = 'S')) then
	
	ds_erro_w := verifica_decimal_dose_prot_onc(nr_seq_paciente_p, nr_seq_material_p, ds_erro_w, qt_dose_prescr_p, qt_dose_p);

	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then
		nr_seq_erro_w := gerar_erro_pac_prot(nr_seq_paciente_p, nr_seq_material_p, 20, ds_erro_w, cd_perfil_w, nm_usuario_p, nr_seq_erro_w);
	end if;	
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_regra_prot_medic ( nr_seq_paciente_p bigint, nr_seq_material_p bigint, nm_usuario_p text, nr_seq_regra_p bigint default 0, nr_ciclo_p bigint default 0, ie_forma_consistencia_p text default 'X', cd_material_p bigint default null, cd_unid_med_prescr_p text default null, qt_dose_prescr_p bigint default null, ie_via_aplicacao_p text default null, ds_observacao_p text default null, ds_ciclos_aplicacao_p text default null, is_paciente_medic_update text default 'N', qt_dias_util_p bigint default null, ie_local_adm_p text default null, ie_uso_continuo_p text default null, cd_intervalo_p text default null, qt_dose_p bigint default null, ds_justificativa_p bigint default null, ds_dose_diferenciada_p text default null, cd_unid_med_dose_p text default null) FROM PUBLIC;

