CREATE OR REPLACE PROCEDURE consistir_regra_setor_agenda (cd_pessoa_fisica_p text, cd_agenda_p bigint, dt_agenda_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text, ie_consistencia_p INOUT text) AS $body$
DECLARE

 
cd_setor_agenda_w	integer;
nr_seq_regra_w		bigint;
qt_agendamento_w	bigint;
qt_dias_regra_w		smallint;
ie_forma_consistencia_w	varchar(1);
	
C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	agenda_regra_setor 
	where	cd_setor_atendimento = cd_setor_agenda_w 
	order by 1;
	

BEGIN 
 
select	max(cd_setor_agenda) 
into STRICT	cd_setor_agenda_w 
from	agenda 
where	cd_agenda = cd_agenda_p;
 
if (cd_setor_agenda_w IS NOT NULL AND cd_setor_agenda_w::text <> '') then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		nr_seq_regra_w := nr_seq_regra_w;
		end;
	end loop;
	close C01;
	 
	if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then 
		select	max(qt_dias_regra), 
			max(ie_forma_consistencia) 
		into STRICT	qt_dias_regra_w, 
			ie_forma_consistencia_w 
		from	agenda_regra_setor 
		where	nr_sequencia = nr_seq_regra_w;
	end if;
	 
	select	count(*) 
	into STRICT	qt_agendamento_w 
	from	agenda a, 
		agenda_consulta b 
	where	a.cd_agenda = b.cd_agenda 
	and	b.dt_agenda between dt_agenda_p and trunc(dt_agenda_p + qt_dias_regra_w) + 83699/86400 
	and	b.ie_status_agenda <> 'C' 
	and	b.cd_pessoa_fisica = cd_pessoa_fisica_p	 
	and	a.cd_setor_agenda = cd_setor_agenda_w;
	 
	if (qt_agendamento_w = 0) then 
		select	count(*) 
		into STRICT	qt_agendamento_w 
		from	agenda a, 
			agenda_consulta b 
		where	a.cd_agenda = b.cd_agenda 
		and	b.dt_agenda between trunc(dt_agenda_p - qt_dias_regra_w) and dt_agenda_p + 83699/86400 
		and	b.ie_status_agenda <> 'C' 
		and	b.cd_pessoa_fisica = cd_pessoa_fisica_p	 
		and	a.cd_setor_agenda = cd_setor_agenda_w;
	end if;
	 
	if (qt_agendamento_w >= 1) then 
		ie_consistencia_p := ie_forma_consistencia_w;
	end if;
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
