CREATE OR REPLACE PROCEDURE consistir_regra_valor_invalido (cd_estabelecimento_p bigint, ie_valor_p text, vl_valor_p text, ie_valido_p INOUT text) AS $body$
DECLARE


cd_perfil_ativo_w	bigint;


BEGIN

if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (ie_valor_p IS NOT NULL AND ie_valor_p::text <> '') and (vl_valor_p IS NOT NULL AND vl_valor_p::text <> '') then

	cd_perfil_ativo_w	:= coalesce(obter_perfil_ativo, 0);

	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ie_valido_p
	from	regra_valor_invalido
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	ie_valor		= ie_valor_p
	and	vl_valor		= vl_valor_p
	and	coalesce(cd_perfil, cd_perfil_ativo_w) = cd_perfil_ativo_w;

else

	ie_valido_p := 'S';

end if;

end;
$body$
LANGUAGE PLPGSQL
