SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_responsavel_legal ( nr_atendimento_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE

 
ds_retorno_w		varchar(255) := '';
dt_alta_w			timestamp;
ie_responsavel_w		varchar(15);
ds_idade_w		varchar(15);


BEGIN 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	Select	Obter_Idade_PF(cd_pessoa_fisica,clock_timestamp(),'A'), 
		dt_alta, 
		ie_responsavel 
	into STRICT	ds_idade_w, 
		dt_alta_w, 
		ie_responsavel_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	if (coalesce(dt_alta_w::text, '') = '') and ((ds_idade_w)::numeric  < 18 ) and (ie_responsavel_w not in ('4','5','8','9','10','11','2077') )then 
	 
		ds_retorno_w := 'O paciente é menor de 18 anos e não está acompanhado do seu responsável legal.'||CHR(10)||CHR(13)||' Somente poderá ser liberado na presença do responsável.';
	 
	end if;
 
 
end if;
 
ds_retorno_p := ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
