SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_result_pacote_conta ( nr_interno_conta_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE



pr_prejuizo_w		real;
nr_seq_proc_pacote_w	bigint;

pr_pacote_w		double precision;

c01 CURSOR FOR
SELECT nr_seq_procedimento
from	Atendimento_Pacote a,
	procedimento_paciente b
where a.nr_seq_procedimento	= b.nr_sequencia
  and (a.nr_seq_procedimento IS NOT NULL AND a.nr_seq_procedimento::text <> '')
  and b.nr_interno_conta	= nr_interno_conta_p;


BEGIN

select coalesce(max(b.pr_perm_prej_pacote),0)
into STRICT pr_prejuizo_w
from parametro_faturamento b,
	conta_paciente a
where a.cd_estabelecimento = b.cd_estabelecimento
  and a.nr_interno_conta = nr_interno_conta_p;

open c01;
loop
	fetch c01 into nr_seq_proc_pacote_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	select	((dividir(sum(coalesce(vl_pacote,0)), sum(coalesce(vl_original,0))) * 100)-100)
	into STRICT	pr_pacote_w
	from (	SELECT	a.vl_procedimento vl_pacote,
			coalesce(b.vl_procedimento,0) vl_original
		FROM procedimento_paciente a
LEFT OUTER JOIN proc_paciente_valor b ON (a.nr_sequencia = b.nr_seq_procedimento AND 1 = b.ie_tipo_valor)
WHERE a.nr_seq_proc_pacote <> a.nr_sequencia and coalesce(a.nr_seq_proc_pacote,0) = nr_seq_proc_pacote_w and a.nr_interno_conta = nr_interno_conta_p
		
union all

		SELECT	a.vl_material,
			a.vl_tabela_original vl_original
		from	material_atend_paciente a
		where coalesce(a.nr_seq_proc_pacote,0) = nr_seq_proc_pacote_w
		  and a.nr_interno_conta = nr_interno_conta_p) alias10;

	RAISE NOTICE '%', nr_interno_conta_p;

	RAISE NOTICE '%', pr_pacote_w;


	if (pr_pacote_w < 0) and (abs(pr_pacote_w) > pr_prejuizo_w) then
		ds_erro_p := ds_erro_p || '2092 ';
		exit;
	end if;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
