CREATE OR REPLACE PROCEDURE consistir_sala_agecons (nr_seq_sala_p bigint, dt_agenda_p timestamp, nr_minuto_duracao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_Seq_Agenda_p bigint) AS $body$
DECLARE


ie_consistencia_w	varchar(1)	:= 'N';
dt_agenda_w		timestamp;
nr_duracao_w		bigint;

C01 CURSOR FOR
	SELECT	dt_agenda,
		nr_minuto_duracao
	from	agenda_consulta
	where	(dt_agenda between dt_agenda_p and dt_agenda_p + (nr_minuto_duracao_p / 1440) - (1/86399) or
		dt_agenda_p between dt_agenda and dt_agenda + (nr_minuto_duracao / 1440) - (1/86399))
	and	nr_seq_sala = nr_seq_sala_p
	and	ie_status_agenda not in ('C','L')
	and	nr_sequencia	<> nr_Seq_Agenda_p
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	dt_agenda_w,
	nr_duracao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ie_consistencia_w	:= 'S';

	end;
end loop;
close C01;

if (ie_consistencia_w = 'S') then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(262408);
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
