CREATE OR REPLACE PROCEDURE consistir_segmento_301 ( nr_seq_estrut_dataset_p bigint, nr_seq_dataset_p bigint, ie_segmento_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_estrut_arq_w	bigint;
vl_retorno_w		varchar(255);
vl_resultado_w		varchar(255);
ds_sql_w		varchar(500);
c001			integer;

cCampos CURSOR FOR
SELECT	nm_tabela,
	nm_atributo,
	ds_campo_301
from	c301_estrutura_campo_seg
where	nr_seq_estrut_dataset = nr_seq_estrut_dataset_p
and	ie_obrigatorio = 'S';

cCampos_w	cCampos%rowtype;


BEGIN

select	max(a.nr_seq_estrut_arq)
into STRICT	nr_seq_estrut_arq_w
from	d301_arquivo_envio a,
	d301_dataset_envio b
where	b.nr_seq_arquivo 	= a.nr_sequencia
and	b.nr_sequencia		= nr_seq_dataset_p;

open cCampos;
loop
fetch cCampos into
	cCampos_w;
EXIT WHEN NOT FOUND; /* apply on cCampos */

	ds_sql_w := 	' select to_char('|| cCampos_w.nm_Atributo ||') from '||cCampos_w.nm_tabela||
			' where nr_seq_dataset = '|| nr_seq_dataset_p;

	c001	:= dbms_sql.open_cursor;
	dbms_sql.parse(c001,ds_sql_w,dbms_sql.native);
	dbms_sql.define_column(c001, 1, vl_retorno_w, 255);
	vl_resultado_w	:= dbms_sql.execute(c001);
	vl_resultado_w	:= dbms_sql.fetch_rows(c001);
	dbms_sql.column_value(c001,1,vl_retorno_w);
	dbms_sql.close_cursor(c001);

	if (coalesce(trim(both vl_retorno_w)::text, '') = '') then

		--O campo #@DS_CAMPO#@, n√£o foi informado para o segmento #@IE_SEGMENTO#@ deste dataset.
		CALL GERAR_D301_DATASET_INCONSIST(	nr_seq_dataset_p,
						'E',
						wheb_mensagem_pck.GET_TEXTO(996262,'DS_CAMPO='||cCampos_w.ds_campo_301||';IE_SEGMENTO='||ie_segmento_p||';'),
						nm_usuario_p,
						ie_segmento_p);

	end if;

end loop;
close cCampos;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
