CREATE OR REPLACE PROCEDURE consistir_setor_lista_espera (cd_setor_atendimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
cd_setor_atendimento_w	integer;
ds_setor_atendimento_w	varchar(255);
				

BEGIN 
 
ds_erro_p := '';
 
select	max(cd_setor_atendimento) 
into STRICT	cd_setor_atendimento_w 
from	agenda_lista_espera a, 
	agenda b 
where	a.cd_agenda = b.cd_agenda 
and	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p 
and	a.cd_setor_atendimento 	<> cd_setor_atendimento_p 
and	a.ie_status_espera	= 'A' 
and	b.cd_tipo_agenda	= 5;
 
if (cd_setor_atendimento_w > 0) then 
	select	max(ds_setor_atendimento) 
	into STRICT	ds_setor_atendimento_w 
	from	setor_atendimento 
	where	cd_setor_atendimento = cd_setor_atendimento_w;
	 
	ds_erro_p := ds_setor_atendimento_w;
	 
end if;
 
end;
