SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_sexo_unid_reserva ( nr_seq_unidade_p bigint, cd_pessoa_fisica_p text, ds_consistencia_p INOUT text) AS $body$
DECLARE

			
ie_sexo_unidade_w	varchar(1);
ie_sexo_paciente_w	varchar(1);
cd_unidade_basica_w	varchar(10);	
cd_unidade_compl_w	varchar(10);	
nr_atendimento_w	bigint;
cd_Setor_Atendimento_w	bigint;
cd_genero_unidade_w	unidade_atendimento.NR_SEQ_GENERO%type;
cd_genero_paciente_w	pessoa_fisica_aux.NR_SEQ_GENERO%type;


BEGIN

ds_consistencia_p := '';

if (coalesce(nr_seq_unidade_p,0) > 0) and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	select	cd_unidade_basica,
		cd_unidade_compl,
		cd_Setor_Atendimento
	into STRICT	cd_unidade_basica_w,
		cd_unidade_compl_w,
		cd_Setor_Atendimento_w
	from	unidade_atendimento
	where	nr_seq_interno = nr_seq_unidade_p;
	
	if (cd_unidade_basica_w IS NOT NULL AND cd_unidade_basica_w::text <> '') then
	
		select	Obter_Sexo_PF(Obter_Dados_Atendimento(max(nr_atendimento),'CP'),'C'),
				max(nr_seq_genero)
		into STRICT	ie_sexo_unidade_w,
				cd_genero_unidade_w
		from	unidade_atendimento
		where	cd_unidade_basica = cd_unidade_basica_w
		and	cd_unidade_compl  = cd_unidade_compl_w
		and	cd_Setor_Atendimento = cd_Setor_Atendimento_w;
		

		if (ie_sexo_unidade_w = 'F') or (ie_sexo_unidade_w = 'M') then
			
			select	max(ie_sexo)
			into STRICT	ie_sexo_paciente_w
			from 	pessoa_fisica
			where	cd_pessoa_fisica = cd_pessoa_fisica_p;
			
			select 	max(nr_seq_genero)
			into STRICT	cd_genero_paciente_w
			from 	pessoa_fisica_aux
			where 	cd_pessoa_fisica = cd_pessoa_fisica_p;
			
			if (ie_sexo_unidade_w <> upper(ie_sexo_paciente_w)) and (obter_se_quarto_familiar(cd_Setor_Atendimento_w,cd_unidade_basica_w,cd_unidade_compl_w,cd_pessoa_fisica_p) = 'N') and (cd_genero_unidade_w <> cd_genero_paciente_w or coalesce(cd_genero_unidade_w::text, '') = '') then
				ds_consistencia_p := OBTER_DESC_EXPRESSAO(727600);
			else
				ds_consistencia_p := '';
			end if;
		end if;
	
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
