CREATE OR REPLACE PROCEDURE consistir_se_exibe_tipo_atend ( nr_atendimento_p bigint, ie_tipo_atendimento_p text, ds_consistencia_p INOUT text) AS $body$
DECLARE

 
qt_atendimento_w	bigint;
ds_tipo_atendimento_w	varchar(254);
						

BEGIN 
 
select	count(*) 
into STRICT	qt_atendimento_w 
from	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p 
and	obter_se_contido(ie_tipo_atendimento,ie_tipo_atendimento_p) = 'S';
 
if (qt_atendimento_w = 0) then 
	select	obter_valor_dominio(12,ie_tipo_atendimento) 
	into STRICT	ds_tipo_atendimento_w 
	from	atendimento_paciente 
	where	nr_atendimento	=	nr_atendimento_p;
 
	ds_consistencia_p	:= WHeb_mensagem_pck.get_texto(306450, 'DS_TIPO_ATENDIMENTO_W=' || ds_tipo_atendimento_w); -- Atendimentos do tipo #@DS_TIPO_ATENDIMENTO_W#@ não estão disponíveis para consulta. 
end if;
 
end;
$body$
