SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_se_mat_prescr_cir ( nr_cirurgia_p bigint, cd_material_p bigint, ds_inconsistencia_p INOUT text ) AS $body$
DECLARE


nr_prescricao_w	bigint;
qt_possui_mat_prescr_cir  bigint;


BEGIN

if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then


	select  max(nr_prescricao)
	into STRICT 	nr_prescricao_w
	from    cirurgia
	where   nr_cirurgia = nr_cirurgia_p;

	if (nr_prescricao_w > 0) then

		select count(*)
		into STRICT   qt_possui_mat_prescr_cir
		from   prescr_material
		where  nr_prescricao = nr_prescricao_w
		and    cd_material = cd_material_p;
	end if;



	if (coalesce(qt_possui_mat_prescr_cir,0) = 0) then
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(288615);
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
