-- DATASOURCE: dbi:Oracle:host=srv-dbora-03.whebdc.com.br;service_name=DEV_1815;port=1521

SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_tit_pagar_baixa (nr_titulo_p bigint, vl_baixa_p INOUT bigint, vl_saldo_multa_p INOUT bigint, vl_multa_p INOUT bigint, vl_saldo_juros_p INOUT bigint, vl_juros_p INOUT bigint, vl_saldo_titulo_p INOUT bigint, dt_baixa_p INOUT timestamp, vl_outras_despesas_p INOUT bigint, vl_outros_acrescimos_p INOUT bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ds_erro_w			varchar(254);
ie_trib_atualiza_saldo_w		varchar(5);
ie_trib_saldo_tit_nf_w		varchar(5);
vl_baixa_w			double precision;
vl_juros_w			double precision;
vl_multa_w			double precision;
vl_alterado_w			double precision;
vl_adiantamento_w			double precision;
vl_desconto_w			double precision;
vl_titulo_w			double precision;
vl_saldo_juros_w			double precision;
vl_saldo_multa_w			double precision;
vl_saldo_titulo_w			double precision;
vl_tributo_w			double precision;
vl_imposto_w			double precision;
nr_repasse_terceiro_w		bigint;
nr_seq_nota_fiscal_w		bigint;
nr_seq_lote_res_pls_w		bigint;
nr_ordem_compra_w		bigint;
qt_trib_repasse_w			bigint;
qt_trib_nf_w			bigint;
qt_trib_lote_pag_pls_w		bigint;
nr_seq_pls_pag_prest_w		bigint;
qt_trib_pls_pag_prest_w		bigint;
qt_trib_ordem_w			bigint;
cd_estabelecimento_w		smallint;
dt_baixa_w			timestamp;
dt_ultima_baixa_w			timestamp;
vl_outras_despesas_w		double precision;
vl_outros_acrescimos_w		double precision;
ie_permite_outras_despesas_w	varchar(1);
ie_soma_outras_desp_saldo_w		varchar(1);
vl_total_baixa_w			double precision;
cont_adiantamento_w				bigint;


BEGIN
ds_erro_w			:= null;

/* francisco - os 72663 - 26/10/2007 - deve tratar ambas as datas, baixa e do adiantamento */

select	max(dt_ultima_baixa)
into STRICT	dt_ultima_baixa_w
from	(SELECT	max(dt_baixa) dt_ultima_baixa
	from	titulo_pagar_baixa
	where	nr_titulo = nr_titulo_p
	and	dt_atualizacao = (select max(dt_atualizacao) from titulo_pagar_baixa where nr_titulo = nr_titulo_p)
	
union all

	SELECT	max(dt_contabil) dt_ultima_baixa
	from	titulo_pagar_adiant
	where	nr_titulo	= nr_titulo_p) alias5;

if (coalesce(dt_ultima_baixa_w::text, '') = '') then
	dt_ultima_baixa_w	:= clock_timestamp();
end if;

/* francisco - os 168616 - 24/12/2009 - tratei o vl cotacao */

select	coalesce(sum(dividir_sem_round(vl_baixa,CASE WHEN coalesce(vl_cotacao_moeda,0)=0 THEN 1  ELSE vl_cotacao_moeda END )),0),
	coalesce(sum(vl_juros),0),
	coalesce(sum(vl_multa),0),
	coalesce(sum(vl_descontos),0),
	coalesce(sum(vl_imposto),0)
into STRICT 	vl_baixa_w,
	vl_juros_w,
	vl_multa_w,
	vl_desconto_w,
	vl_imposto_w
from	titulo_pagar_baixa
where	nr_titulo = nr_titulo_p;

vl_baixa_w	:= vl_baixa_w + coalesce(vl_baixa_p, 0);

select	coalesce(sum(vl_alteracao - vl_anterior),0)
into STRICT	vl_alterado_w
from	titulo_pagar_alt_valor
where	nr_titulo = nr_titulo_p;

select	coalesce(sum(vl_adiantamento),0)
into STRICT	vl_adiantamento_w
from	titulo_pagar_adiant
where	nr_titulo	= nr_titulo_p;

select	vl_titulo,
	vl_saldo_juros,
	vl_saldo_multa,
	vl_saldo_titulo,
	nr_repasse_terceiro,
	nr_seq_nota_fiscal,
	cd_estabelecimento,
	nr_seq_lote_res_pls,
	nr_seq_pls_pag_prest,
	coalesce(vl_outras_despesas,0),
	coalesce(vl_outros_acrescimos,0),
	nr_ordem_compra
into STRICT	vl_titulo_w,
	vl_saldo_juros_w,
	vl_saldo_multa_w,
	vl_saldo_titulo_w,
	nr_repasse_terceiro_w,
	nr_seq_nota_fiscal_w,
	cd_estabelecimento_w,
	nr_seq_lote_res_pls_w,
	nr_seq_pls_pag_prest_w,
	vl_outras_despesas_w,
	vl_outros_acrescimos_w,
	nr_ordem_compra_w
from	titulo_pagar
where	nr_titulo	= nr_titulo_p;

ie_permite_outras_despesas_w	:= coalesce(obter_valor_param_usuario(851,193, obter_perfil_ativo, obter_usuario_ativo, cd_estabelecimento_w), 'N');
ie_soma_outras_desp_saldo_w	:= coalesce(obter_valor_param_usuario(851,256, obter_perfil_ativo, obter_usuario_ativo, cd_estabelecimento_w), 'N');

begin
select	ie_trib_atualiza_saldo,
	coalesce(ie_trib_saldo_tit_nf,'N')
into STRICT	ie_trib_atualiza_saldo_w,
	ie_trib_saldo_tit_nf_w
from	parametros_contas_pagar
where	cd_estabelecimento	= cd_estabelecimento_w;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(189078);
end;

vl_tributo_w	:= 0;
if (ie_trib_atualiza_saldo_w = 'S') then
	select	count(*)
	into STRICT	qt_trib_repasse_w
	from	repasse_terceiro_venc b,
		repasse_terc_venc_trib a
	where	a.nr_seq_rep_venc	= b.nr_sequencia
	and	b.nr_repasse_terceiro	= nr_repasse_terceiro_w;

	/* francisco - os 185542 - considerar no saldo, quando nao havia tributo na nf */

	select	count(*)
	into STRICT	qt_trib_nf_w
	from	nota_fiscal_trib a
	where	a.nr_sequencia	= nr_seq_nota_fiscal_w;

	if (qt_trib_nf_w = 0) then
		select	count(*)
		into STRICT	qt_trib_nf_w
		from	nota_fiscal_item_trib a
		where	nr_sequencia	= nr_seq_nota_fiscal_w;
	end if;

	if (qt_trib_nf_w = 0) then
		select	count(*)
		into STRICT	qt_trib_nf_w
		from	nota_fiscal_venc_trib b,
			nota_fiscal_venc a
		where	a.nr_sequencia	= nr_seq_nota_fiscal_w
		and	a.nr_sequencia	= b.nr_sequencia;
	end if;

	select	count(*)
	into STRICT	qt_trib_lote_pag_pls_w
	from	pls_lote_protocolo_venc b,
		pls_lote_prot_venc_trib a
	where	a.nr_seq_lote_venc	= b.nr_sequencia
	and	b.nr_seq_lote		= nr_seq_lote_res_pls_w
	/* francisco - 06/07/2010 - os 227380 - tive que fazer o tratamento abaixo por causa da geracao dos vencimentos e tributos pelo baca */

	and	exists (SELECT	1
			from	titulo_pagar_imposto x
			where	x.nr_titulo	= b.nr_titulo
			and	x.dt_atualizacao_nrec > a.dt_atualizacao_nrec);
			
	select	count(*)
	into STRICT	qt_trib_pls_pag_prest_w		
	from	pls_pag_prest_venc_trib	c,
		pls_pag_prest_vencimento	b,
		pls_pagamento_prestador	a
	where	a.nr_sequencia	= b.nr_seq_pag_prestador
	and	b.nr_sequencia	= c.nr_seq_vencimento
	and	a.nr_sequencia	= nr_seq_pls_pag_prest_w;
	
	select	count(*)
	into STRICT	qt_trib_ordem_w
	from	ordem_compra_item_trib a
	where	nr_ordem_compra = nr_ordem_compra_w;

	/* francisco - os 121297 - 17/12/2008 - incluir o qt_trib_repasse_w */

	if (coalesce(nr_repasse_terceiro_w,0) = 0 or qt_trib_repasse_w = 0) and		-- edgar , qdo o titulo e gerado o sistema gera o titulo com valor limpo
		(coalesce(nr_seq_nota_fiscal_w,0) = 0 or qt_trib_nf_w = 0) and (coalesce(nr_seq_lote_res_pls_w,0) = 0 or qt_trib_lote_pag_pls_w = 0) and (coalesce(nr_seq_pls_pag_prest_w,0) = 0 or qt_trib_pls_pag_prest_w = 0) and (coalesce(nr_ordem_compra_w,0) = 0 or (qt_trib_ordem_w = 0))then
		select	coalesce(sum(a.vl_imposto),0)
		into STRICT	vl_tributo_w
		from	tributo b,
			titulo_pagar_imposto a
		where	a.nr_titulo		= nr_titulo_p
		and	a.cd_tributo		= b.cd_tributo
		and	a.ie_pago_prev		= 'V'
		and	coalesce(b.ie_saldo_tit_pagar,'S')	= 'S';
		
	/* francisco - os 80633 - 29/02/2008 */

	else
		if (ie_trib_saldo_tit_nf_w = 'N') then
			select	coalesce(sum(vl_imposto),0)
			into STRICT	vl_tributo_w
			from	titulo_pagar_imposto a,
				tributo b
			where	a.cd_tributo = b.cd_tributo
			and	nr_titulo	= nr_titulo_p
			and	ie_pago_prev	= 'V'
			and	(nr_seq_baixa IS NOT NULL AND nr_seq_baixa::text <> '')
			and	coalesce(b.ie_saldo_tit_pagar,'S')	= 'S';
		else
			select	coalesce(sum(vl_imposto),0)
			into STRICT	vl_tributo_w
			from	titulo_pagar_imposto a,
				tributo b
			where	a.cd_tributo = b.cd_tributo
			and	nr_titulo	= nr_titulo_p
			and	ie_pago_prev	= 'V'
			and	coalesce(b.ie_saldo_tit_pagar,'S')	= 'S';
		end if;		
	end if;
end if;

select	count(*)
into STRICT	cont_adiantamento_w
from	titulo_pagar_adiant
where	nr_titulo = nr_titulo_p;

if	(ie_permite_outras_despesas_w = 'S' AND cont_adiantamento_w > 0) or (ie_soma_outras_desp_saldo_w = 'S') then
	vl_total_baixa_w	:= coalesce(vl_titulo_w,0) + coalesce(vl_alterado_w,0) - coalesce(vl_tributo_w,0) - coalesce(vl_adiantamento_w,0) + coalesce(vl_outras_despesas_w,0) + coalesce(vl_outros_acrescimos_w,0);
else
	vl_total_baixa_w	:= coalesce(vl_titulo_w,0) + coalesce(vl_alterado_w,0) - coalesce(vl_tributo_w,0) - coalesce(vl_adiantamento_w,0);
end if;

if (vl_baixa_w > vl_total_baixa_w) then
	ds_erro_w	:=	' ' || Wheb_mensagem_pck.get_texto(355681) || chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355683) || ': ' ||  nr_titulo_p || chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355684) || ': ' || vl_titulo_w || chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355685) || ': ' || vl_saldo_titulo_w || chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355686) || ': ' || vl_baixa_w || chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355687) || ': ' || vl_desconto_w || chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355688) || ': ' || vl_imposto_w;
end if;

if	(ie_permite_outras_despesas_w = 'S' AND cont_adiantamento_w > 0) or (ie_soma_outras_desp_saldo_w = 'S') then
	vl_saldo_titulo_w	:= coalesce(vl_titulo_w,0) + coalesce(vl_alterado_w,0) - coalesce(vl_baixa_w,0) - coalesce(vl_tributo_w,0) - coalesce(vl_adiantamento_w,0) + coalesce(vl_outras_despesas_w,0) + coalesce(vl_outros_acrescimos_w,0);
else
	vl_saldo_titulo_w	:= coalesce(vl_titulo_w,0) + coalesce(vl_alterado_w,0) - coalesce(vl_baixa_w,0) - coalesce(vl_tributo_w,0) - coalesce(vl_adiantamento_w,0);
end if;

if (vl_saldo_titulo_w < 0) then
	ds_erro_w	:=	' ' || Wheb_mensagem_pck.get_texto(355689) || chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355683) || ': ' || nr_titulo_p ||  chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355684) || ': ' || vl_titulo_w ||  chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355690) || ': ' || vl_alterado_w ||  chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355686) || ': ' || vl_baixa_w ||  chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355691) || ': ' || vl_tributo_w ||  chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355692) || ': ' || vl_adiantamento_w || chr(13) ||
				' ' || Wheb_mensagem_pck.get_texto(355688) || ': ' || vl_imposto_w;
end if;

ds_erro_p := substr(ds_erro_w,1,120);
vl_baixa_p	:= vl_baixa_w;
vl_saldo_multa_p	:= vl_saldo_multa_w;
vl_multa_p	:= vl_multa_w;
vl_saldo_titulo_p	:= vl_saldo_titulo_w;
dt_baixa_p	:= dt_ultima_baixa_w;
vl_saldo_juros_p	:= vl_saldo_juros_w;
vl_juros_p		:= vl_juros_w;
vl_outras_despesas_p	:= vl_outras_despesas_w;
vl_outros_acrescimos_p	:= vl_outros_acrescimos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_tit_pagar_baixa (nr_titulo_p bigint, vl_baixa_p INOUT bigint, vl_saldo_multa_p INOUT bigint, vl_multa_p INOUT bigint, vl_saldo_juros_p INOUT bigint, vl_juros_p INOUT bigint, vl_saldo_titulo_p INOUT bigint, dt_baixa_p INOUT timestamp, vl_outras_despesas_p INOUT bigint, vl_outros_acrescimos_p INOUT bigint, ds_erro_p INOUT text) FROM PUBLIC;

