CREATE OR REPLACE PROCEDURE consistir_todas_prescr ( ds_var_consistir_toda_prescr_p text, nr_prescricao_p bigint, cd_perfil_p bigint, nm_usuario_p text, ie_erro_p INOUT text, qt_material_p INOUT bigint) AS $body$
DECLARE

 
ds_erro_w		varchar(255);
ds_usuario_w		varchar(255);
ds_perfil_w		varchar(255);
ds_proced_glosa_w		varchar(255);
ie_erro_w			varchar(15)	:= '';
qt_material_w		bigint	:= 0;
				

BEGIN 
 
if (ds_var_consistir_toda_prescr_p = 'S')then 
 
	consistir_prescricao(nr_prescricao_p, cd_perfil_p, nm_usuario_p, ds_erro_w, ds_usuario_w, ds_perfil_w, ds_proced_glosa_w);
 
end if;
 
ie_erro_w	:= obter_se_prescr_conformidade(nr_prescricao_p);
 
if (ie_erro_w = 'S')then 
 
	select 	count(*) 
	into STRICT	qt_material_w 
	from 	prescr_material 
	where 	nr_prescricao = nr_prescricao_p 
	and 	ie_erro = 125 
	and 	ie_suspenso = 'N';
 
end if;
 
ie_erro_p		:= ie_erro_w;
qt_material_p	:= qt_material_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
