CREATE OR REPLACE PROCEDURE consistir_usuario_ciente_rec ( nr_prescricao_p bigint, nr_seq_recomendacao_p bigint, cd_pessoa_fisica_p text, ie_ciente_p INOUT text, dt_horario_p timestamp) AS $body$
DECLARE


qt_ciente_w	bigint := 0;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_recomendacao_p IS NOT NULL AND nr_seq_recomendacao_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	coalesce(count(*),0)
	into STRICT	qt_ciente_w
	from	prescr_mat_alteracao a
	where	a.nr_prescricao			= nr_prescricao_p
	and		a.nr_seq_recomendacao	= nr_seq_recomendacao_p
	and		a.cd_pessoa_fisica		= cd_pessoa_fisica_p
	and		a.ie_alteracao			= 7
	and		((coalesce(a.nr_seq_horario_rec::text, '') = '') or (exists (SELECT	1
					from	prescr_rec_hor b
					where	b.nr_prescricao			= a.nr_prescricao
					and		b.nr_seq_recomendacao	= a.nr_seq_recomendacao
					and		b.nr_sequencia			= a.nr_seq_horario_rec
					and		b.dt_horario			= dt_horario_p
					and		coalesce(b.ie_horario_especial,'N') = 'S'
					and		(b.dt_fim_horario IS NOT NULL AND b.dt_fim_horario::text <> ''))));
end if;

if (qt_ciente_w > 0) then
	ie_ciente_p	:= 'S';
elsif (qt_ciente_w = 0) then
	ie_ciente_p	:= 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
