CREATE OR REPLACE PROCEDURE consistir_venc_repasse (nr_repasse_old_p bigint, nr_repasse_new_p bigint, vl_repasse_old_p bigint, vl_repasse_new_p bigint, vl_liberado_old_p bigint, vl_liberado_new_p bigint) AS $body$
